css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
  /*
   * TUMBLRTALE HEADER THEME v1.10.2
   * BY THESILVERINFINITY
  */

   /* CURSOR STYLE */  
	.l-header-container * {  cursor:url('/*[[cursor]]*/'),auto; }
	  
	/* HEADER CONTAINER */
	.l-header-container--refresh .tab_bar .tab_home .tab_anchor:before{
	  content:  none;
	}

	.l-header-container {
	  background: url('https://live.staticflickr.com/65535/48714496652_fa6b301f08_o.png') repeat-y !important;
						  
	  background-color: black !important;
	  height: 65px;
	  border-bottom-style: solid;
	  border-bottom-width: 2px;
	  border-bottom-color: #5e8d69;
	}

	/* LOGO */

	#logo a{
	  background-image: url('https://live.staticflickr.com/65535/48714497847_9feddca3b1_o.png') !important;
	}
	h1.logo > a > div  {
		display: none;
	}
	.svg-logo {
	  display: none;
	}

	.logo-anchor, .logo:active {
	  background: url('https://live.staticflickr.com/65535/48714497847_9feddca3b1_o.png') no-repeat;
	  position: fixed !important;
	  display: block !important;
	  height: 70px !important;
	  width: 200px !important;
	  top: 13px !important;
	  left: 0px !important;
	  z-index: 10 !important;
	  transform: none;
	}

	/* SEARCH FIELD */  
	#search_field{
	  position: relative;
	  margin-left: 180px;
	} 
	  
	#search_query{
	  position: relative;
	  border: 2px solid white;
	  background: black;
	  color: white;
	  height: 41px;
	  font-size: 18px;
	  
	}

	.search_form_field:before{
	  position: relative;
	  content: url('https://live.staticflickr.com/65535/48714497467_33220ca890_o.png') !important;
	}


	.ui_search{
	  top: 4px;
	}

	.search_popover, .search_popover .popover, .search_popover popover_gradient{
	  position: relative;
	  left: 180px !important;
	  top: 5px !important;
	  border: 2px solid #5e8d69; 
	  padding:0px;
	  background: black;
	  color: white;
	}

	/* TAB BUTTONS DEFAULT*/  
	.tab-bar-container{
	  position: relative !important;
	  
	}


	#home_button .tab_anchor ,#discover_button .tab_anchor, #inbox_button .tab_anchor,#messaging_button .tab_anchor, #activity_button .tab_anchor, #new-xkit-control .tab_anchor, #xkit-control .tab_anchor, #account_button .tab_anchor, #xkit_button .tab_anchor {
	  margin-top: 17px;
	  border: 2px solid #fb8229;
	  opacity: 1.0;
	  margin-right:5px;
	  cursor: url('/*[[cursor]]*/'),auto; 
	  background-color: black;
	  margin-right: 17px;
	}

	/* TAB BUTTONS HOVER*/
	#home_button:hover .tab_anchor:hover, #discover_button:hover .tab_anchor:hover, #inbox_button:hover .tab_anchor:hover,#messaging_button .tab_anchor:hover, #activity_button:hover .tab_anchor:hover, #new-xkit-control:hover .tab_anchor:hover, #xkit-control .tab_anchor:hover, #account_button:hover .tab_anchor:hover, #xkit_button .tab_anchor:hover{
	  border-color: #fffd3f;
	  margin-right: 17px;
	}

	/* NOTICE BUTTONS */
	div .tab_notice, .tab-notice--outlined, #new_post_notice_container > span{
	  background: black;
	  border-color: #fb8229;
	  color: #fb8229;
	}

	.tab_anchor:before{
	  display: none;
	}

	/* DASHBOARD BUTTON (Home2)*/  
	#home_button .tab_anchor {
	  background: black url('https://live.staticflickr.com/65535/48714000238_1502fcc4ef_o.png') no-repeat !important;
	  width: 30px !important;
	  height: 21px !important;
	}

	#home_button:hover .tab_anchor:hover{
	  background: black url('https://live.staticflickr.com/65535/48714000243_95a9ba2fdd_o.png') no-repeat !important;
	}

	#new_post_notice_container{
	  background: black;
	  left: 55px !important;
	  top: 5px;
	  right: auto !important;
	  transform: scale(.75,.75);
	}

	/*DISCOVER BUTTON (Map2)*/  
	#discover_button .tab_anchor {
	  background: black url('https://live.staticflickr.com/65535/48714496297_529ddea312_o.png') no-repeat !important;
	  width: 25px !important;
	  height: 21px !important; 
	}
	#discover_button .tab_anchor:hover {
	  background: black url('https://live.staticflickr.com/65535/48714000048_9f21a22a5b_o.png') no-repeat !important;
	}

	/* INBOX BUTTON (Mail2)*/
	#inbox_button .tab_anchor{
	  background: black url('https://live.staticflickr.com/65535/48714496347_7ac838eac4_o.png') no-repeat !important;
	  height: 21px !important;
	  width: 40px !important;
	}

	#inbox_button .tab_anchor:hover {
	  background: black url('https://live.staticflickr.com/65535/48714000128_cd36986a39_o.png') no-repeat !important;
	}
	#inbox_button .tab_notice , div.tab_notice:nth-child(2) > span:nth-child(1){
	  left: 65px !important;
	  top: 5px;
	  right: auto !important;
	  transform: scale(.75,.75);
	  background: black;
	  color: #fb8229;
	}

	/* MESSAGING BUTTON (Cell2)*/
	#messaging_button .tab_anchor{
	  background: black url('https://live.staticflickr.com/65535/48714000333_2d76352347_o.png') no-repeat !important;
	  height: 25px !important;
	  width: 70px !important;
	}

	#messaging_button .tab_anchor:hover{
	  background: black url('https://live.staticflickr.com/65535/48714000348_984d5a1682_o.png') no-repeat !important;
	  
	}

	#messaging_button .tab_notice{
	  left: 60px !important;
	  top: 5px;
	  right: auto !important;
	  transform: scale(.75,.75);
	  background: black;
	  color: #fb8229;
	}

	/* ACTIVITY BUTTON (Act2)*/
	#activity_button .tab_anchor{
	  background: black url('https://live.staticflickr.com/65535/48713998848_35515d53f5_o.png') no-repeat !important;
	  height: 21px !important;
	  width: 25px !important;
	}

	#activity_button .tab_anchor:hover {
	  background: black url('https://live.staticflickr.com/65535/48714495087_7338486d7b_o.png') no-repeat !important;
	}

	/* NEW XKIT BUTTON */
	#new-xkit-control.christmas > a, #new-xkit-control .tab_anchor, #xkit_button .tab_anchor {
	  background: black url('https://live.staticflickr.com/65535/48714332251_9d79036b56_o.png') no-repeat !important;
	  width: 65px !important;
	  height: 25px !important;
	  margin-left: 0px !important;
	  margin-right: 10px !important;
	}
	#xkit_button > button::before {
	  display: none;
	}
	 #new-xkit-control .tab_anchor:hover, #xkit_button .tab_anchor :hover {
	   background: black url('https://live.staticflickr.com/65535/48714498397_f15eeabfa4_o.png') no-repeat !important;
	 }

	#xkit-control .tab_anchor{
	  background: black url('https://live.staticflickr.com/65535/48714332251_9d79036b56_o.png') no-repeat !important;
	  width: 63px !important;
	  height: 25px !important;
	}
	 #xkit-control .tab_anchor:hover {
	   background: black url('https://live.staticflickr.com/65535/48714498397_f15eeabfa4_o.png') no-repeat !important;
	 }

	/* ACCOUNT BUTTON (Item2)*/
	#account_button .tab_anchor{
	  background: black url('https://live.staticflickr.com/65535/48714496447_b79ac76e67_o.png') no-repeat !important;
	  height: 25px !important;
	  width: 72px !important;
	}

	#account_button .tab_anchor:hover {
	  background: black url('https://live.staticflickr.com/65535/48714496467_3e58a1e11c_o.png') no-repeat !important;
	}

	#account_button .tab_anchor .icon_user_settings{
	  display: none;
	}

	/* COMPOSE BUTTON */
	.compose-button{
	  fill: #fb8229;
	  top: 13px;
	  transform: scale(.75,.75);
	}
	.compose-button:hover{
	  fill: #fffd3f;
	}
}
