css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("gzgreg.github.io") {

html, body {
  background: #060606 !important;
  color: #AAA !important;
}

.ui-tabs .ui-tabs-panel {
  background: #161616 !important;
  border-width: 0;
  padding: none !important;
}

#navbar a {
  color: #AAA !important;
}

#tabs .ui-tabs-nav .ui-state-default a {
  color: #AAA !important;
  background: #252525 !important;
  border: 1px #fff !important;
}
  
.ui-widget-content {
  background: #161616 !important;
  border: 1px solid #000;
  color: #999;
}

#tabs .ui-widget-header {
  border-color: #000 !important;
}

.button, .buttonLit {
  border: 1px solid black;
  background: #161616 !important;
}

.buttonLit {
  background-color: #161616 !important;
  border-color: #000 !important;
  color: #aa3333 !important;
}

.buttonLit:Hover {
  background-color: #161616 !important;
  border-color: #000 !important;
  color: #FF3333 !important;
}  
  
#tabs .ui-tabs-nav .ui-state-active a {
  color: #aa3333 !important;
  background: #333 !important;
}

#tabs.ui-tabs.ui-widget.ui-widget-content.ui-corner-all ul.ui-tabs-nav.ui-helper-reset.ui-helper-clearfix.ui-widget-header.ui-corner-all li.ui-state-default.ui-corner-top {
  background: #0000FF !important;
}

#moneyButtonText {
  background: #222 !important;
  color: #33aa33 !important;
}
  
#moneyButtonText:Hover {
  background: #222 !important;
  color: #00FF00 !important;
}

#tabs .ui-tabs-nav .ui-state-default a:Hover {
  background: #222 none repeat scroll 0 0 !important;
  border: 1px none #FF0000 !important;
  color: #aaa !important;
}

#tabs .ui-tabs-nav .ui-state-active a:Hover {
  background: #222 none repeat scroll 0 0 !important;
  color: #FF3333 !important;
}

div#numToBuyContainer p input#numToBuy {
  color: #aaa !important;
  background: #222 !important;
  border-radius: 1px !important;
  border: 1px #aaa !important;
}

html body div#container div#tabs.ui-tabs.ui-widget.ui-widget-content.ui-corner-all div#inventory.ui-tabs-panel.ui-widget-content.ui-corner-bottom div#numToBuyContainer p span.buttonLit.submitNumToBuy {
  color: #aaa !important;
  background: #222 !important;
  border-radius: 1px !important;
  border: 1px #aaa !important;
}
}
