css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://muropaketti.com/"), url-prefix("http://muropaketti.com/artikkelit/"), url-prefix("http://muropaketti.com/uutiset/"), url-prefix("http://muropaketti.com/kategoria/"), url-prefix("http://muropaketti.com/taskumuro"), url-prefix("http://muropaketti.com/dome") {

body {
    background: #1F252D none repeat scroll 0% 0%;
}    
    
body #wrapper {
    background: #2A2A2A none repeat scroll 0% 0%;
}
    
body #topbar {
    background: transparent url("http://otavafiles.fi/plaza/uploads/images/muro_subnavi_bg.png");
    background-color: #20262E; 
}
    
body .navtabs li {
    background: #333 none repeat scroll 0% 0%;
}
    
body .navtabs {
    background: #333 none repeat scroll 0% 0%;
}    
    
body .navtabs li.active {
    background: #394859 none repeat scroll 0% 0%;
}
    
body .navi li.current-menu-item {
    background: transparent;
}
    
body .navi li.current-menu-item a {
    color: #FFF;
}    

body .navi li {
    color: #327BAE;
}    
    
body .navi li a:hover {
    background: rgba(0, 0, 0, 0.2) none repeat scroll 0% 0%; 
    text-decoration: none;
}    
    
body .navi li:hover {
    color: #FFF;
    background: #394859 none repeat scroll 0% 0%;
} 
 
body .navi .sub-menu {
    background: #394859 none repeat scroll 0% 0%;
}    

body .navi .sub-menu li {
    color: #FFF;
} 
    
body.archive #main-articles {
    margin-bottom: 30px;
}    
 
#wrapper {    
    max-width: 1360px;
    padding: 20px 10px 0px;
}

#inner-wrapper {
    padding: 0px 0px;
    margin-top: 110px;
    max-width: 1360px;    
}
    
.wrapper--center {
    margin: 0px 1px auto;
}
    
.col-two {
    max-width: 900px;
}
   
.column-left {
    max-width: 900px;
    margin-top: 10px;
}

.topbar__logo {
    background: transparent url("http://muropaketti.com/wp-content/themes/muropaketti/assets/img/muropaketticom.png") no-repeat;
    background-size: 380px 94px;
    margin-left: 10px;
    width: 650px;
    height: 92px;
} 
    
#topbar .menu-item:hover, #topbar .menu-item-blogit:hover {
    background-color: transparent;
}    
    
.navi {
    letter-spacing: 1px;
    text-transform: uppercase;
    margin: 0px 0px;
    width: 100%;
    position: fixed;
    top: 92px;
    height: 41px;
    background: #262626 url("http://murobbs.muropaketti.com/styles/default/xenforo/gradients/navigation-tab.png") repeat-x scroll 0px -40px;
} 
    
.navi li a {    
    border-left: 1px solid rgba(0, 0, 0, 0.4);
    border-right: 1px solid rgba(255, 255, 255, 0.2);
    line-height: 41px;
    padding-right: 14px;
    padding-left: 14px;
    font-weight: bold;
    font-size: 14px;
    vertical-align: text-bottom;
    text-align: center;
    font-family: verdana,arial,sans-serif;
    text-shadow: 0px 0px 0px transparent, 1px 1px 1px #000;
    color: #2276B1;
}
    
.navi.float--right {
    font-size: 12px;
    height: 41px;
    line-height: 41px;
    position: fixed;
    right: 0px;
    border-left: 1px solid rgba(0, 0, 0, 0.4);
    width: 180px;
}    
    
.navi li.menu-item-has-children:hover {
    color: #FFF;
    background-color: transparent !important;
}
    
.navi li.menu-item-has-children::after {
    top: 15px;
    font-size: 12px;
    right: 2px;
}    
    
.navi li .sub-menu {    
width: 235px;
}    
    
.navi li .sub-menu > li:hover {
    background-color: #394859 !important;
    color: #FFF;
}
    
.navi li .sub-menu li a {
    padding: 5px 10px;
    font-size: 12px !important;
}    
   
.navi li {
    padding: 0px 0px;  
}
    
.navi li:hover > .sub-menu {
    top: 40px;    
}
    
.navi li.current-menu-parent {
    background: #262626 none repeat scroll 0% 0%;
}

.navi li.current-menu-parent a {
    color: #FFF;
}    
    
.navi li.current-menu-parent a:hover {
    color: #FFF;
}
    
.navi__magazine {
    padding-top: 12px;
    padding-left: 10px;
    line-height: 20px;
}
  
.navi__search--button {
    font-size: 20px;
    line-height: 0px;
    padding-top: 7px;
}   
    
.navtabs {
    margin-bottom: 0px;
}
    
.navtabs li:hover {
    background: #327BAE none repeat scroll 0% 0%;
} 
    
.block-header {
    background: #394859 none repeat scroll 0% 0%;
}  
  
ul.list {
    background: #222 none repeat scroll 0% 0%;
}  

ul.list.gridWidget-postContainer {
    background: #222 none repeat scroll 0% 0% !important;
}
    
ul.list > li {
    border-bottom: 3px solid #3C5078;
}    
    
ul.list > li.post-previewTemplate .list-meta, ul.list > li.template-fullpost .list-meta {
    padding-left: 10px;
}    

.widget-suositut__tabs li.active {
    background: #394859 none repeat scroll 0% 0%;
}
    
.widget-suositut__tab {
    background: #222 none repeat scroll 0% 0%;
}
    
.widget-suositut__tab ul li {
    border-bottom: 1px solid #3C5078;
}    

.widget-suositut__tabs li:last-child {
    background: #222 none repeat scroll 0% 0%;
}    
 
.widget-feed ul .feed__item, .widget-feed ul .comment__item, .widget-comments ul .feed__item, .widget-comments ul .comment__item {
    border-bottom: 1px solid #3C5078;
}    
    
.box--half {
    background: #222 none repeat scroll 0% 0%;  
    margin-bottom: 10px;
} 
    
.templateSwitchList {
    top: -30px !important;
}
  
.templateSwitchList li {
    color: #CCC;
}

.templateSwitchList li:hover, .templateSwitchList li.active {
    color: #327BAE;
}  
    
.template-fullpost .meta {
    padding-left: 10px;
}    
  
#tilaa-seuraa {
    top: 90px;
    z-index: 99;
    position: fixed;
    margin-bottom: 2px;
    width: 0px;
    height: 41px;
    line-height: 41px;
    background: transparent url("http://plaza.fi/s/img2/bg/muro_nav_bg.gif") repeat-x scroll center top;
}  
    
/* .some {
    position: fixed;
    top: 92px;
    right: 180px;
    width: 400px;
    height: 42px;
    padding-top: 7px;
    background: transparent !important;
}  */      
    
#tilaa-seuraa .links {
    display: none;  
}
   
#tilaa-seuraa A.fa.fa-facebook {
    position: fixed;
    top: 92px;
    right: 305px;
    width: 20px;
    height: 42px;
    padding-top: 7px;
    background: transparent !important;
}    
    
#tilaa-seuraa A.fa.fa-twitter {
    position: fixed;
    top: 92px;
    right: 265px;
    width: 20px;
    height: 42px;
    padding-top: 7px;
    background: transparent !important;
}    
    
#tilaa-seuraa A.fa.fa-youtube {
    position: fixed;
    top: 92px;
    right: 225px;
    width: 20px;
    height: 42px;
    padding-top: 7px;
    background: transparent !important;
}    
    
#tilaa-seuraa A.fa.fa-rss {
    position: fixed;
    top: 92px;
    right: 185px;
    width: 20px;
    height: 42px;
    padding-top: 7px;
    background: transparent !important;
}        
    
#tilaa-seuraa A.fa.fa-facebook:hover {
    color: #3b5998;  
} 
    
#tilaa-seuraa A.fa.fa-twitter:hover {
    color: #00aced;    
} 
    
#tilaa-seuraa A.fa.fa-youtube:hover {
    color: #C4302B;    
}     

#tilaa-seuraa A.fa.fa-rss:hover {
    color: #FA9B39;    
} 

.sidebar-block {
    margin-bottom: 0px;
}  

.article__body a {
    text-decoration: none;
}    

.carousel-item {
    width: 900px;
}    
    
a {
    color: #2072AA;
}    
    
a:hover, a:active, a:focus {
    color: #2072AA;    
    text-decoration: underline;
}
    
.grid__container .post__content p a {
    color: #2072AA;
} 
    
.widget-feed .typogr__showmorebtn {
    color: #2072AA;
}  
    
.typogr__showmorebtn:hover {
    color: #2072AA;
    text-decoration: underline;
}  
    
.typogr__catlink {
    color: #3275A6;
}    
    
.widget-bigCarousel-thumbs.slick-initialized.slick-slider {
    display: none;
} 
    
.widget-bigCarousel, .widget-bigCarousel-single {
    margin-bottom: 0px;
} 
    
h2 {
    padding-left: 10px;
}    
   
h2.block-header {
    margin-bottom: 0px;
}
    
input[type="submit"], input[type="reset"], input[type="button"], input[type="file"], .button, button {
    font-family: "Hind",sans-serif;
    font-size: 12px;
    text-transform: uppercase;
    color: #FFF;
    background: #666 none repeat scroll 0% 0%;
    padding: 1px 1px 1px;
    height: 40px;
    line-height: 40px;
    cursor: pointer;
    border: 1px solid transparent;
    box-sizing: border-box;
    transition: all 0.3s ease 0s;
    letter-spacing: 0.00em;
    font-weight: 600;
    display: initial;
    background: #2A2A2A none repeat scroll 0% 0%;
}   
    
input[type="submit"]:hover, input[type="submit"]:focus, input[type="reset"]:hover, input[type="reset"]:focus, input[type="button"]:hover, input[type="button"]:focus, input[type="file"]:hover, input[type="file"]:focus, .button:hover, .button:focus, button:hover, button:focus {
    background: #2A2A2A none repeat scroll 0% 0%;
    color: #FFF;
}    
    
.button.commentsBtn {
    background: #394859 none repeat scroll 0% 0%;
    color: #FFF;
} 
 
.button.commentsBtn:hover {
    background: #327BAE  none repeat scroll 0% 0%;
    color: #FFF;
}
    
.gform_wrapper .gform_footer input.button, .gform_wrapper .gform_footer input[type="image"], .gform_wrapper .gform_footer input[type="submit"] {
    background-color: #394859;
}        
      
.slick-dots li.slick-active button {
    background: red none repeat scroll 0% 0%;
}    
   
.widget-suositut__tab ul li h3 {
    color: #c7c7c7;
}    
    
.widget-area h3 {
    font-size: 15px;
    line-height: 1.1;
    letter-spacing: 0em;
    color: #c7c7c7;
}    
    
.gridWidget-postContainer > li p {
    font-size: 15px;
    line-height: 1.2;
    letter-spacing: 0.016em;
    color: #C7C7C7;
}
    
.gridWidget-postContainer li .typogr__catlink {
    color: #327BAE;
}
    
.gridWidget-postContainer > li .post__content {
    padding-left: 10px;
    padding-right: 10px;
}    

body #footer {
    background: #1F252D none repeat scroll 0% 0%;
    color: #FFF;
}    
    
#footer a {
    color: #FFF;
}   
    
#footer a:hover {
    text-decoration: underline;
    color: #2372AA;
} 
    
#sitewide-searchbar-container {
    background: #394859 none repeat scroll 0% 0%;
    box-shadow: 0px 0px 0px 0px #CCC inset;
}   
    
.widget-hintafi form:not(.gpoll) button {
    background: rgb(57, 72, 89) none repeat scroll 0% 0%;
}  
    
.widget-viikonkysely .gfield_radio input[type="radio"]:checked + label::before {
    background-color: #327BAE;
} 
    
.ad.topad {
    margin: 15px auto 0px;
}    
    
.grid--fourArticlesContainer ul {
    background-color: #222;
} 
    
blockquote {
    border-left: 10px solid #327BAE;
}
    
blockquote::before {
    color: #327BAE;
}
    
blockquote::after {
    color: #327BAE;
}

/*DOME korjaukset */
.omwidget, .sidebar-block, .textwidget {
    margin-bottom: 0px;
}
.block-header {
    color: #fff;
}

    
}
