css:
@-moz-document domain("mail.google.com") {
.yw-inbox-dashboard-collapsed .yw-logo .trial-badge {
  display: none;
}

.yw-inbox-dashboard-collapsed > ul > li:nth-child(5) {
  display: none;
}

.yw-inbox-dashboard-collapsed > ul > li:nth-child(6) {
  display: none;
}

.yw-sidebar-get-started {
  display: none;
}

.yw-inbox-dashboard-expanded.yw-dashboard-top-positioned footer .yw-footer-badge {
  display: none;
}

.yw-inbox-dashboard-expanded.yw-dashboard-top-positioned footer .yw-footer-content {
  display: none;
}

.yw-send-later-button, .yw-attach-tracked-file {
  display: none;
}

div.yesware-composer-container section.toolbar-left div.crm-toggle {
  display: none;
}

div.yesware-composer-container .yw-divider.yw-divider-right {
  display: none;
}

.yw-sidebar-emails-crm {
  display: none;
}

.yw-logo {
  display: none;
}

.yw-inbox-dashboard-expanded.yw-dashboard-top-positioned > header .yw-message-filter {
  left: 15px;
}

.yw-inbox-dashboard-collapsed > ul {
  padding-top: 9px;
  left: -1px;
  border: 0px transparent hidden;
}

.yw-dashboard-top-positioned {
  background-color: transparent;
  border: 0px transparent hidden;
}
.yw-inbox-dashboard-expanded {
  background-color: #fff;
}

.yesware-top {
  margin-right: 15px !important; 
}

.yw-inbox-dashboard-collapsed.yw-dashboard-top-positioned, .yw-inbox-dashboard-collapsed.yw-dashboard-top-positioned > ul {
  border: 0px transparent hidden;
  border-top: 0px transparent hidden;
  border-bottom: 0px transparent hidden;
}

.yw-inbox-dashboard-collapsed.yw-dashboard-top-positioned > ul > li {
    border-radius: 2px;
    border: 1px solid rgba(0,0,0,0.1);
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.85),rgba(247,247,247,.85));
    padding: 0px 8px 0px 8px;
    height: 27px;
    font-size: 11px;
    line-height: 27px;
    margin-right: 16px;
}
.yw-inbox-dashboard-collapsed.yw-dashboard-top-positioned > ul > li:hover {
    border: 1px solid #c6c6c6;
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.9),rgba(247,247,247,.9));
}
.yw-expand{
    margin-top: -9px;
}
}
