css:
@-moz-document domain("7cups.com") {
 /* by shade */
    /*@import url(https://fonts.googleapis.com/css?family=Inconsolata:400,300,700);*/
    /*@import url(https://fonts.googleapis.com/css?family=Ubuntu:400,300,700);*/
    /*html, *
{
	/*font-family: 'Inconsolata' !important;
}*/
    
    .navbar {
        background: #000 !important;
        background-image: none;
    }
    .forumPost,
    .forumPostHead a,
    .form-group label {
        color: #ffffff !important;
    }
    .bootbox-body {
        color: #ffffff !important;
    }
    body,
    .container-fluid,
    .hidden-sm-down {
        background-color: #17191A !important;
    }
    #bottomStatus,
    .heavyLoad {
        color: #fff !important;
        background-color: #089FD1 !important;
    }
    .btn-default,
    .btn-primary {
        color: #fff !important;
        background-color: #17191A !important;
    }
    .card .nav-link,
    .active {
        color: #fff !important;
        background-color: #2B2F30 !important;
    }
    .card {
        background-color: #2B2F30 !important;
    }
    .feedPost-content,
    .userImage {
        color: #fff !important;
    }
    .feedUser a,
    .feedUser a:hover,
    .feedUser a:visited {
        color: #fff !important;
    }
    .offlineMessageDiv,
    #offlineMessageDiv {
        color: #fff !important;
    }
    .alert {
        color: #fff !important;
    }
    .alert a {
        color: #fff !important;
    }
    #bottomStatus,
    #bottomStatus .container,
    #bottomStatus .container .row {
        background-color: #2B2F30 !important;
        width: 100%;
        text-align: center;
        margin-left: auto;
        margin-right: auto;
    }
    #modal-header p,
    .modal-header p {
        color: #fff !important;
    }
    #modal-body,
    .molda-body {
        color: #fff !important;
    }
    #newListPersonalStatusButton button {
        border: none !important;
        color: #fff !important;
        background-color: #17191A !important;
        font-weight: bold;
    }
    a .clearfix {
        color: #fff !important;
        width: 103%;
        height: 150%;
        background-color: #011 !important;
    }
    .label-warning {
        background-color: #999 !important;
        color: #fff !important;
    }
    .tabpanel a {
        color: #fff !important;
        border: 1px solid #eee;
    }
    .convHolder,
    #actionButtons,
    #actionButtons img {
        background-color: #2B2F30 !important;
    }
    .convHolder .note {
        color: #FFFFFF !important;
    }
    .card-title {
        color: #ffffff !important;
    }
    textarea {
        background-color: #2B2F30 !important;
        color: #fff !important;
    }
    .youWrap,
    .meWrap {
        color: #000 !important;
    }
    .text-center,
    .details {
        color: #fff !important;
    }
    #MessageScrollDivResize,
    #MessageScrollDiv {
        border: none !important;
    }
    .modal-content {
        background-color: #2B2F30 !important;
    }
    .modal-content a {
        color: #fff !important;
    }
    .modal-title {
        color: #fff !important;
        background: inherit !important;
    }
    .listenerData {
        color: #fff !important;
    }
    table .listenerData .data {
        color: #fff !important;
    }

}
