/* lowendtalk darker & wider page theme */
/* (c) Copyright by Fusl                */
/* version 1.0.6                        */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lowendtalk.com") {
  /* version 1.0 */
  body {
    background: #111;
    border-top: none;
    color: #FFF;
  }
  #Frame {
    background: #222;
    margin: 0px auto;
    border-radius: 0px;
    box-shadow: none;
    width: 100%;
  }
  .Row {
    width: 95%;
    min-width: 1020px;
  }
  .DiscussionTabs {
    background-color: #333 !important;
    border-bottom: 1px solid #0AF !important;
  }
  .Tabs,
  .Headings,
  .PageTitle,
  .Message,
  .CommentHeading,
  .DP_ResultsForm {
    color: #FFF;
  }
  .Signature {
    color: #CCC;
    border: none;
  }
  .RoleTitle, .ThankedBy {
    color: #888;
  }
  a, a.UserLink {
    color: #0AF !important;
  }
  a:hover, a.UserLink:hover {
    color: #07C !important;
  }
  input.DateBox, input.InputBox, input.SmallInput, textarea, BarWrap, .CommentLink {
    background-color: #444 !important;
    color: #FFF !important;
    border: none !important;
  }
  code, pre, .BarWrap {
    background-color: #333;
    border: none;
  }
  .Box, .BoxFilter,  .FilterMenu li, .PanelInfo li {
    background: none !important;
    border: none !important;
  }
  h1, h2, h3, h4, .Title, .ButtonBarMarkupHint, .H {
    color: #AAA;
  }
  .Sprite, .Sprite16, .SpNotifications, .ButtonBar .ButtonWrap, .OptionsTitle {
    filter: invert(100%);
    -webkit-filter: invert(100%);
  }
  .twitter-follow-button {
    display: none;
  }
  .Category {
    background-color: #333 !important;
    border: none !important;
  }
  .HasNew {
    background-color: #550;
  }
  .Read {
    background-color: #1D1D1D !important;
  }
  .Pager .Highlight {
    background: none !important;
    border-bottom: 1px solid #0AF !important;
  }
  .Pager span:hover, .Pager a:hover {
    background: none;
  }
  .TagCloud li {
    background: none !important;
    border: none;
  }
  .FlyoutMenu, .Flyout {
    background-color: #2A2A2A !important;
    border: none !important;
    color: #FFF;
  }
  .MenuItems a, .MenuItems a:link, .MenuItems a:visited {
    color: #FFF !important;
  }
  .About dd {
    color: #AAA;
  }
  .SiteSearch #Form_Search {
    padding: 5px;
  }
  .SiteSearch #Form_Go {
    display: none;
  }
  #Foot {
    display: none;
  }
  .FlagContent {
    color: #800 !important;
  }
  /* version 1.0.1 */
  mark {
    color: #FF0;
    background: none;
  }
  /* version 1.0.2 */
    .Preview {
    background: none;
  }
  /* version 1.0.3 make header fixed */
  #Head {
    background-color: #333;
    position: fixed;
    width: 100%;
    z-index: 1;
  }
  #Body {
    width: auto;
    margin: 0px auto;
    padding-top: 50px;
  }
  /* version 1.0.4 make mebox fixed */
  .MeBox {
    position: fixed;
    width: 260px;
    background-color: rgb(34, 34, 34);
    z-index: 1;
    margin-top: -50px;
  }
  #Panel {
    padding-top: 50px;
  }
  /* version 1.0.5 make popup backgrounds dark */
  div.Popup .Body {
    background-color: #222;
  }
  /* version 1.0.6 fix "More Comments" button */
  .Button, .NavButton, .MorePager a {
    background: #333 !important;
    text-shadow: none !important;
    border: none !important;
    box-shadow: none !important;
    color: #0AF !important;
  }
}
