css:
@-moz-document domain("tipsport.cz"), domain("tipsport.sk") {
body {
   /* background-color: #22262b; */
    background-color: #294D7A;
    color: #000;
}

#menuFilter {
    color: #FFFFFF;
}

#forum, #forumLive {
    color: #22262b;
}

.forumControls {
    background-color: #5D5D5D;
}

#forum #comments {
    background-color: #1e88e5;
}

.noGuarantee {
    color: #FFF;
}

/*Tiket*/

.popupBody {
    background: #1E88E5;
}

.ticketDetail {
    background-color: #1E88E5;
}

a, .link, a:visited, .link:visited {
    color: #000;
}

tbl td, .externalArticleBody table td {
    color: #FBFBFB;
}

.tbl tr:hover td, .externalArticleBody tr:hover td {
    background-color: #FFAE19;
}

.btn:hover {
    background: #FFAE19;
}

.ticketSum {
    border-top: 10px solid #FFAE19;
}

.tblTicketSum td {
    color: #FFF;
}

.tbl td, .externalArticleBody table td {
    color: #000;
}

.ticketHead {
    border-bottom: 10px solid #FFAE19;
}

/*Kurzy*/

.tblOdds,.tblOddsExpanded {
   background-color: #46A9FF;
}

/*SLider*/

#promoSlider {
background-color: #1e88e5;
}

#promoSlider h2 {
    color: #FFF;
}

.promo-wrap .otherArticles {
color: #FFF;
}

.promo-wrap .readMore {
color: #FFF;
}

.oddsButtons{
background-color: #0095FF;
}

.tblOdds:not(.tblOddsCompact) .rowMatch:hover, .tblOdds #superOfferId .rowMatch:hover+.rowGoals {
background: #F89B1C;
}

.colEvent a {
color: #000;
}

#promoSlider .jquery-slider-pages {
background-color: #1e88e5;
}

.textPaneMenu {
    background: #F89B1C;
}

#oddsMenu {
    background-color: #F89B1C;
}

.tab li.selected, .tab li.act {
    border-bottom: 2px solid #FFF;
}

#topMatches .rowTips th {
    background-color: #1e88e5;
}

/*Filter panel*/

.filterPane {
    background-color: #1e88e5;
}

.tfw_quickOptions .btnLink {
    color: #FFF;
}

.tfw_minuteOldItem {
    color: #000;
}

/*Tiket arena hlavní strana*/

#ticketArenaId .ticketInfo {
    border: 1px solid #000;
}

/*Panely homepage, analýzy, tiketaréna*/

.textPane, .textPaneBlock {
    background-color: #1E88E5;
}

/*Avatar color*/

.avatar a.username, .link {
    color: #000;
}

/*Avatar analýza color*/

.analyzeBody .avatar a.username {
    color: #fff;
}

/*Analýza nadpis*/

.tblAnalWrap .analyzeTitle span {
    color: #FFFFFF;
}

/*Analýza hover*/

.tblAnalWrap:not(.detailWrap):hover {
    background: rgb(129, 137, 144);
}

/*Text článku*/

.externalArticleBody p {
    color: #FFF;
}

/*Číslo zisku u analýz*/
.colProfile .analWin {
    color: yellow;
}

/*Tiket hlavní*/
#ticketContentId {
    background-color: #46A9FF;
}

/*Nadpis u promo kurtzů*/
.promoOdds .colCompetition {
    color: #000;
}

/*Tlačítka kurzů*/
.tblOdds .rate:hover {
    background: #1E88E5;
}

.tblOdds .rate {
    background-color: #294D7A;
    color: #FFFFFF;
}

.tblOddsGoals .rate .value {
    color: #FFF;
}

.tblOddsGoals .rate .name, .tblOddsGoals .rate:hover.selected .name {
    color: #FFF;
}

/*Menu se sportama
.zippySuperSportClass {
    background: #1E88E5;
}
*/


/*Vybranej kurz v nabídce*/
.colOnTicket .rate, .colOnTicket .rate:hover, .tblOddsGoals .colOnTicket .rate:hover .name, .tblOddsGoals .colOnTicket .rate .value, .tblOddsGoals .colOnTicket .rate:hover .value {
    background-color: #F89B1C;
}
}
