css:
@-moz-document domain(52pojie.cn){
body {background:  rgba(229, 237, 242,1);}
#scbar {
    overflow: hidden;
    height: 42px;
    line-height: 42px;
    border-top: 0px solid #FFF;
    border-bottom: 1px solid #E9EFF5;
    background: #E8EFF5;
}
.plc{background-color:rgb(194, 213, 227) !important;}
p{font-family:\u534E\u6587\u6977\u4F53,\u6977\u4F53,serif !important;}
.t_f{font-size:30px !important;color:rgba(26, 102, 125, 0.890196) !important}
.bm {background: rgba(229, 237, 242,0)!important;}
#nv{border-radius: 7px!important;z-index:299;}
.comiis_nav {
    border-radius: 42px;
    width: 810px;
}
.a_mu{background-color: rgb(229, 237, 242)!important;border-width: 0px;}
.comiis_nav {border-radius: 80px 25px 80px 25px; margin:0px auto 0px auto!important;width: 952px!important;position:relative;z-index:199;}
        #anchor,#quickpost, #f_post, #f_pst, #fast_post_c {
            position:fixed !important;
            border:1px solid #51AFF7 !important;
            left: auto !important;
            width: auto !important;
            min-width: 75% !important;
            bottom:0px !important;
            top: 85% !important;
            padding:0 !important;
            margin:0 !important;     
            background:#F9F9F9 !important;
            z-index:99 !important;
            opacity: 0.1 !important;
        }
        #anchor:hover,#quickpost:hover, #f_pst:hover, #f_post:hover, #fast_post_c:hover {
            top: auto !important; 
            opacity: 1 !important;
        }
    #f_pst .p_pop, #f_pst .p_opt{
        position: absolute !important;
        bottom: 0 !important;
        top: auto !important;
    }
.wrap > #header,#wrapA > #header,
#hd{height:90px !important;overflow:hidden !important;-webkit-transition: .1s ease-in;-o-transition: .1s ease-in;-moz-transition: .1s ease-in;}
.wrap > #header:hover,#wrapA > #header:hover,
#hd:hover{height:230px !important;-webkit-transition: .2s ease-in;-o-transition: .2s ease-in;-moz-transition: .2s ease-in;}
.avatars ,.avatars *{background-image: none  !important;}
a.p_author_face img,
.avatar img ,
.user-pic img.pic,
.readFace +  a[href^="u.php"] > img.pic
{max-width:155px !important;border-radius:16px;box-shadow:0px 0px 0px #888 !important;padding:0px !important;}
table:hover  .avatar img ,
table:hover .user-pic img.pic,
table:hover .readFace +  a[href^="u.php"] > img.pic
{border-radius:16px;box-shadow:0px 0px 0px #668 !important}
a.p_author_face img{box-shadow:3px 2px 6px #bbb !important}
tbody[id^="normalthread_"]:hover,
tbody[id^="stickthread_"]:hover,
.threadCommon > table > tbody#threadlist > tr.tr3:hover,
tbody[id^="normalthread_"]:hover > tr > th,
tbody[id^="normalthread_"]:hover *,
tbody[id^="stickthread_"]:hover *,
.threadCommon > table > tbody#threadlist > tr.tr3:hover *
{background:#fdd !important;
-o-transition: .2s ease-in;-moz-transition: .2s ease-in;-webkit-transition: .2s ease-in;}
}
