css:
@-moz-document domain('vk.com'), domain('www.vk.com') {
  body {
    background-size: cover;
    background-attachment: fixed;
    background-image: url(/*[[bg]]*/);
  }
  * {
    transition: 150ms opacity ease-in-out 0s;
    transition: 100ms background ease-in-out 0s;
  }
  #side_bar ol li .left_row,
  .im_fixed_nav #im_nav_wrap {
    background: transparent;
  }
  #side_bar a {
    color: #DDDEE0;
  }
  #side_bar ol li .left_row {
    border-radius: 2px;
  }
  #side_bar ol li .left_row:hover {
    color: #040404;
  }
  a {
    color: #22486F;
  }
  .left_box{
    background-color: transparent;
    color: #f0f0f0;
  }
  .tabs,
  #side_bar {
	background: rgba(0,0,0, 0.3) !important;
  }
  .dialogs_row {
    background-color: rgba(255,255,255, .8);
  }
  #page_header {
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
    overflow: hidden;
  }
  #wrap1 {
    border: none !important;
    border-style: none !important;
  }
  .tl1, tl2 {
    border: none !important;
    background: transparent;
  }
  ul.t0 a {
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
  }
  #im_write {
    background: transparent;
    color: #fff;
  }
}
