css:
@-moz-document domain("laracasts.com") {
.logo {
    -webkit-filter: grayscale(1);
}

html .Header,
html[class] .Header {
    background: #151515;
    margin-bottom: 40px;
}

.Header .Header__experience {
    background: #444;
}

.Header .Header__experience:before {
    border-right-color: #444;
}

.Banner__footer {
    background: #333;
    border-color: #555;
    padding-top: 14px;
    padding-bottom: 14px;
}

.Card__image {
    display: none;
}

.Card {
    margin-bottom: 20px;
    width: calc(33.33% - 22.5px);
}

.Card__title {
     font-size: 20px;
}

.Card__details {
    padding-top: 30px;
}

.Card__difficulty {
    color: #999;
}

.Card:nth-child(4n+1) {
    clear: none;
}

.Card:nth-child(3n+1) {
    clear: left;
}

.Card:last-child, .Card:nth-child(4n) {
    margin-right: 30px;
}

.Card:last-child, .Card:nth-child(3n) {
    margin-right: 0;
}

.Footer-Secondary {
    background: #eee;
}

.Footer-Secondary .copyright {
    color: #555;
}

@media only screen and (max-width: 1050px) and (min-width: 640px) {
    .Card {
        clear: none !important;
        margin-bottom: 20px;
        margin-right: 30px !important;
        width: calc(49.995% - 20px);
    }
    .Card:nth-child(2n) {
    	margin-right: 0 !important;
	}
}
@media only screen and (max-width: 640px) {
    .Card {
        clear: left !important;
        margin-bottom: 20px;
        margin-right: 0 !important;
        width: 100%;
    }
}
}
