css:
@-moz-document domain("tipsport.cz") {
body {
    background-color: #22262b;
    color: #000;
}

#menuFilter {
    color: #FFFFFF;
}

#forum, #forumLive {
    color: #22262b;
}

.forumControls {
    background-color: #394149;
}

#forum #comments {
    background-color: #1e88e5;
}

#forum #comments .ownComment > .head, #forum #comments .ownComment > .text {
    background-color: #f4b641;
}
    
#forum .comment.ownComment > .triangleTopRight {
    border-top-color: #f4b641; 
}
    
#forum #filters {
    background-color: #394149; 
    
}
    
.pagingWrapTop {
    background-color: #394149;
}
    
.textPane, .textPaneBlock {
    background-color: #394149;

}
    
#messageZone {
    background-color: #394149;
}  
    
.messages {
    background-color: #394149;
     
}
    
#head #jsIcoLowAmountId {
    display: none
        
}
    
.tab .tabC {
   color: #e69424;
    
}
    
label {
  font-size: 13px;
  font-weight: 400;
  color: #e69424;
    
}
    
.noGuarantee {
    color: #FFF;
}

/*Tiket*/

.popupBody {
    background: #1363A9;
}

.ticketDetail {
    background-color: #1363A9;
}

a, .link, a:visited, .link:visited {
    color: #F7F7F7;
}

tbl td, .externalArticleBody table td {
    color: #FBFBFB;
}

.tbl tr:hover td, .externalArticleBody tr:hover td {
    background-color: #FFAE19;
}

.btn:hover {
    background: #FFAE19;
}

.ticketSum {
    border-top: 10px solid #FFAE19;
}

.tblTicketSum td {
    color: #FFF;
}

.tbl td, .externalArticleBody table td {
    color: #FFF;
}

.ticketHead {
    border-bottom: 10px solid #FFAE19;
}

}
