css:
@-moz-document url-prefix("http://dotavideo.ru/forum/view/") {
/*Body and BG*/
html, body, .nforum-bigborder, tr.nforum-mytr2 td, .forum-author, .nforum_table_cat.yellow .yellowtr td
{
background-color: #272727 !important;
color: #BFBFBF;
}

#forumdiv > div.forumSolidTable_container.nforumSolidTable_container > div.nforum-bigborder > table.nforum_table_cat.nforum_table_cat_noborder > tbody > tr > td
{
    background-color: #272727 !important;
}
/*Posts*/
.q-message .q-message, .q-message .q-header, .q-message .q-message .q-message .q-message, .q-message .q-message .q-message .q-header, .q-message .q-message .q-message .q-message .q-message .q-message, .q-message .q-message .q-message .q-message .q-message .q-header
{
    color: #D0D0D0;
    background-color: #333333;
}
.q-message .q-message .q-message, .q-message .q-message .q-header, .q-message .q-message .q-message .q-message .q-message, .q-message .q-message .q-message .q-message .q-header, .q-message .q-message .q-message .q-message .q-message .q-message .q-message, .q-message .q-message .q-message .q-message .q-message .q-message .q-header
{
    background-color: #292929;
    color: #D0D0D0;
}


.q-header, .q-message
{
    background-color: #292929;    
    color: #D0D0D0;
}

.q-message
{
    border: 1px solid #4A4A4A;
    border-top-left-radius: 0;
}
.q-header
{     
    border: 1px solid #4A4A4A;
    border-bottom: 0 none;
}
.nforum-userlogin
{
    background-color: #353535;
    box-shadow: initial;
    border: 1px solid rgba(0, 0, 0, 0.39);
    border-radius: 5px;
}

.spoiler
{
    background-color: #333333;
    border: 1px dashed #4A4A4A;
}

.spoiler_head
{
    color: #4D82A0;
}

table.nforum_table_cat.yellow, .nforum-massagetr td, .nforum_table_cat.yellow .yellowtr td
{
    border-style: none;
}

.nforum-head
{    
    opacity: 0.7;
}

.nforum-mytr .nforum-userlogin
{
	background: #353535;
    /*border: 1px solid rgba(205, 157, 57, 0.36);*/
    border:	1px solid rgba(21, 129, 202, 0.16);
    color: rgba(21, 129, 202, 0.55)!important;
}

#forumdiv .nforum-mytr .nforum-userlogin-left a, #forumdiv .nforum-mytr .nforum-userlogin-left a:visited {
    /*color: #A77B00;*/
    color: rgba(21, 129, 202, 0.55);
}

/*Avatar Oo*/
td.forum-author > div > a > img
{
    -webkit-filter: saturate(50%);
    filter: saturate(50%);
}

td.forum-author > div > a > img:hover
{
    -webkit-filter: initial;
    filter: initial;
}

.nforum-profile-link img
{
    border: none !important;
}

.forum-ustatus2
{
    background: initial;
}

#forumdiv a.forum-ustatus1
{
 background: initial !important;   
}
/*Images in posts*/
td.forum-message img
{
	-webkit-filter: saturate(50%);
    filter: saturate(50%);
}

td.forum-message:hover img
{
    -webkit-filter: initial;
    filter: initial;
}
/*
.nforum-mytr
{
    
}
*/
/* Some stuff*/
.nforum-bigborder
{
    border: 1px solid #2F2F2F;
}

.nforum-bottom-pagi
{
    border-top: 1px solid #2F2F2F;
}

#forumdiv a.pagelink.active
{
	background:   #2476A5;
    color: #EFEFEF;
}

.bbcodes-div a {
    -webkit-filter: invert(90%);
    filter: invert(90%);
}

input[value="Отправить новое сообщение"]
{
    -webkit-filter: invert(87%);
    filter: invert(87%);
}

#topic_ta
{
	background: initial;   
	color: #BFBFBF;
    border: initial;
    box-shadow: inset 0 0 5px #191717;
}

#topic_ta:hover, #topic_ta:active
{
    box-shadow: inset 0 0 5px #0E0D0D;
    border: initial;
}
/*
//bk #272727
// light #353535
*/
}

@-moz-document url-prefix("http://dotavideo.ru/") {
/*Left menu and "head"*/
.onenews-fulldescr, .menu-left nav a, .menu-left .logo
{
    background: initial;
}

.menu-left {
    background-color: rgb(39, 39, 39);
}

.menu-left nav a.without-text {
    filter: invert(90%);
    -webkit-filter: invert(90%);
}

.d2net-video .content > header
{
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.7);
    -webkit-filter: saturate(50%);
    filter: saturate(50%);
}

.menu-left nav a
{
    -webkit-filter: saturate(50%);
    filter: saturate(50%);
}

/*Scrollbar magic*/
::-webkit-scrollbar {
    width: 18px;
}
 
::-webkit-scrollbar-track {
    box-shadow: inset 0 0 6px rgba(0,0,0,0.42); 
    /*border-radius: 5px;*/
}
 
::-webkit-scrollbar-thumb {
    box-shadow: inset 0 0 6px rgba(0,0,0,0.5); 
}


::-webkit-scrollbar-button:increment {
border-style: solid;
border-width: 18px 9px 0 9px;
border-color: #313131 transparent transparent transparent;    
}

::-webkit-scrollbar-button:decrement {
    border-style: solid;
border-width: 0 9px 18px 9px;
border-color: transparent transparent #313131 transparent; 
    
}
}

@-moz-document url("http://dotavideo.ru/") {
/*Main page*/
/*Bgnd on all big elements*/
html, body, #videoaction, #live-stream-1000, #inner-1000
{
background-color: #272727 !important;
color: #f3f3f3;
}

.indexblocks, .indexblocks.newsblockindex,.indexblocks.newsblockindex2
{
    background: initial;
}

#live-stream
{
    background: initial;
}
/*Trashy shadows and span lines*/
#news-leftshadow, #news-rightshadow, .news-borders
{
    display: none;
}

.bottomline
{
    background: initial;
}

body > div.content > section > div.width-all.indexblocks.newsblockindex > div > div > div:nth-child(2)
{
       border-top-style: none !important;
}

/*News block*/
.news-blik
{
        border-radius: 5px 0 0 5px;
}

a.onenews2
{
    -webkit-filter: saturate(50%);
    filter: saturate(50%);
}

a.onenews2:hover
{
    -webkit-filter: initial;
    filter: initial;
}
#drive-buttons span
{
    opacity: 0.5;
}

/*"Monitor"*/
span#stream-refresh
{
    background: url('http://dotavideo.ru/templates/default/images/stream-refresh.png') no-repeat 0 0px;
}

#stream-refresh-gif
{
    display: none;
}

.monitor
{
    border-radius: 5px;
	clip-path: inset(0 0 0 0 round 6px 0px 0px 0px);
    -webkit-clip-path: inset(0 0 0 0 round 6px 0px 0px 0px);
}

.monitor img
{
    -webkit-filter: saturate(50%);
    filter: saturate(50%);
}
/* Odd way to preload image in css >_<'' */
.onlinestreem .monitor img[src='http://dotavideo.ru/uploads/stream/covers/1339249066585.jpg']
{
    background: url('http://i.imgur.com/tffRZ8p.jpg') no-repeat -9999px -9999px;
}

.onlinestreem:hover .monitor img[src='http://dotavideo.ru/uploads/stream/covers/1339249066585.jpg']
{
    -webkit-filter: initial;
    filter: initial;
    content:url('http://i.imgur.com/tffRZ8p.jpg');
}

.onlinestreem:hover .monitor img 
{
    -webkit-filter: initial;
    filter: initial;
    opacity: initial;
}

.streem-descr-streemer, .streem-descr-about, .streem-descr-param
{
 	text-shadow: initial;
}

.streem-descr-streemer
{
	color: #949494;
}

.viewers
{
    -webkit-filter: opacity(60%);
    filter: opacity(60%);
}

.onlinestreem:hover, .onlinestreem:active
{
	background: initial;
	box-shadow: inset 0 0 5px #0E0D0D;
}

#live-stream-1000:hover .index-absolut, .newsblockindex:hover .index-absolut {background: initial;}
.indexblocks:hover .index-absolut a, .inner-absolut a, #live-stream-1000:hover .index-absolut a {color: inherit;}
/*Small streams under "monitor"*/
.sstream
{
    background: initial;
    box-shadow: inset 0 1px 3px #1D1D1D;
    border: 1px solid #191919;
    -webkit-filter: saturate(50%);
    filter: saturate(50%);
}

.sstream-header, .sstream-right, .sstream-watchers, .sstream-cup
{
    color: #6B6B6B !important;
    text-shadow: 0 1px 0 #1B1B1B;
    
}

.sstream-cup
{
    opacity: 0.5;   
}

.sstream-watchers
{
    opacity: 0.68;
}
/*User's news block*/

#fast-botfade, #fast-topfade
{
    background: initial;
}

a.otherlink.other2, #fast_news_submit a
{
	opacity: 0.8;   
}

.fastnews-item-text, .fastnews-item-login a, .fastnews-item-login, .fastnews-itemtime
{
    color: #6B6B6B;
}

.fastnews-item-v2
{
	border-bottom: 1px solid #6b6b6b;
}

#fast_news_ta
{
    background: initial;
    opacity: 0.5
}
/*Last forum messages*/
.fast-forum
{
	border: 1px solid #272727;
	box-shadow: inset 0 0 5px #080808;
    background: initial;
}

.fast-forum:hover
{
	box-shadow: inset 0 0 5px #000;
}

.fast-forum:hover > a
{
    -webkit-filter: initial;
    filter: initial;
}

.forum-lasttopick-a a.fast-forum-a
{
    color: #aaaaaa;
}

.smallfont, a.black-a, a.black-a:visited
{
	color: #888888;
}

.fast-forum > a
{
	-webkit-filter: saturate(50%);
    filter: saturate(50%);
}
/*Video block*/

.video2-new
{
	box-shadow: 0 0 9px #0A0A0A;
}
.video-new
{
    -webkit-filter: saturate(50%);
    filter: saturate(50%);
}

.video-new:hover
{
    -webkit-filter: initial;
    filter: initial;
}

.video-new:hover .video2-new
{
  box-shadow: 0 0 15px #000000; 
}

.indexblocks:hover .index-absolut
{
    background: initial;
}

.h2-videoblock
{
    text-shadow: initial;
}

#video-menu
{
	-webkit-filter: invert(88%);
    filter: invert(88%);
}

.video-descr-new
{
    background: #1F1F1F;
}

.video-descr-new strong a, .video-descr-new strong a:visited
{
    color: #949494;
}

.video-descr-new strong
{
    border-bottom: 1px solid rgba(238, 238, 238, 0.16);
}

}
