css:
@-moz-document url-prefix("http://www.wolframalpha.com/input/") {
body, html {
    /* Remove the too large fixed minimum page width */
	min-width: 0;
}

#navbar > ul#siteNav {
    /* Remove the too large fixed navbar width */
    width: auto;
    /* Remove white space on the right of the navbar */
    padding-right: 0;
    /* Correct a deviation in the centering coordinates */
    margin-left: -348px;
}

#wrap > footer {
    /* Remove the too large fixed minimum footer width */
	min-width: 0;
}
  
#sidebar {
    /* Remove the sidebar (with ads and social site buttons) */
	display: none;
}
}
