#s3gt_translate_tooltip_mini_logo , 
#s3gt_translate_tooltip_mini_logo:hover  {
    background: url(chrome://s3gt/skin/s3gt_logo.png) center no-repeat  !important;   
}
#s3gt_translate_tooltip_mini_sound ,
#s3gt_translate_tooltip_mini_sound:hover {
	background: url(chrome://s3gt/skin/s3gt-tooltip-mini-sound.png) center no-repeat !important;
}
#s3gt_translate_tooltip_mini_sound[is_sound_off="true"] ,
#s3gt_translate_tooltip_mini_sound[is_sound_off="true"]:hover {
	background: url(chrome://s3gt/skin/s3gt-tooltip-mini-sound-off.png) center no-repeat !important;
}
#s3gt_translate_tooltip_mini_copy ,
#s3gt_translate_tooltip_mini_copy:hover {
	background: url(chrome://s3gt/skin/s3gt-tooltip-mini-copy.png) center no-repeat !important;   
}
#s3gt_translate_tooltip_mini_logo_plus ,
#s3gt_translate_tooltip_mini_logo_plus:hover ,
#s3gt_translate_tooltip_mini_sound ,
#s3gt_translate_tooltip_mini_sound:hover ,
#s3gt_translate_tooltip_mini_sound[is_sound_off="true"] ,
#s3gt_translate_tooltip_mini_sound[is_sound_off="true"]:hover ,
#s3gt_translate_tooltip_mini_copy ,
#s3gt_translate_tooltip_mini_copy:hover ,
#s3gt_translate_tooltip_mini_logo , 
#s3gt_translate_tooltip_mini_logo:hover  {
    background-size: 26px 26px !important;             
    width: 26px !important;    
    height: 26px !important;     
}


.s3gt_translate_tooltip[is_mini='true'] {
    opacity: 1  !important;   
/* 	  box-shadow: inset 0 0 0px 22px rgba(255,255,255,0.6) ,
                      0 0 4px 1px rgba(255,255,255,0.9)!important;     
    padding-top: 4px !important;
    padding-bottom: 0px !important;
    padding-left: 4px !important;
    padding-right: 4px !important;   */  
}
.s3gt_translate_tooltip[is_mini='true']:hover {
    opacity: 1  !important;  
}

.s3gt_translate_tooltip_mini {
   border: 2px solid #30527A !important;   /*   #778899 */    
   border-radius: 4px;
   margin: 3px;
   box-shadow: 0 0 5px 2px rgba(122,122,122,0.5);
   filter: saturate(70%) !important; 
}

.s3gt_translate_tooltip_mini:hover {
   /* magenta rgba(167, 83, 200, 0.9) */
	 box-shadow: 0 0 5px 2px rgba(13, 200, 233, 0.9);
   border-color:  #2E5DAE  !important;    
   filter: saturate(180%) !important;  
}




.s3gt_translate_tooltip:not([is_mini='true']) {
	background-color: #F1FaFD   !important;  
	color: #1641C1  !important;  
	font-size: 17px  !important;  
	border: 1px solid #778899 !important;  
  border-radius: 0 !important;  
  box-shadow: 0px 0px 6px 1px rgba(22,22,22,0.5)  !important; 
  opacity: 1 !important;   
}

#s3gt_translate_tooltip_txt_to ,
#s3gt_translate_tooltip_txt_from ,
#s3gt_translate_tooltip_txt_reverse {
    padding-top: 0px !important;
    margin-top: 3px !important;
}
    


#s3gt_translate_tooltip_head {
    background: none  !important;  
    margin-top: -4px !important;
    margin-bottom: -4px !important;
}


#s3gt_translate_tooltip_head_logo, 
#s3gt_translate_tooltip_head_logo:hover {
    margin-bottom: 2px !important;
    box-shadow:   none !important;  
}


#s3gt_translate_tooltip_name {
  color: #000  !important;  
	font-size: 12px  !important;  
}



#s3gt_translate_tooltip_head  div ,
#s3gt_translate_tooltip_head_pin[unpin="true"]
{
    background-color: #fff  ; 
    margin-top: 2px !important;
    box-shadow: none ;
}

.s3gt_translate_tooltip_head_button:hover 
{
  background-color: rgba(210,220,255,0.8) !important;   
  box-shadow: 0 0 2px 1px rgba(0,50,250,0.7) !important;    
}

#s3gt_translate_tooltip_head_close:hover{
	background-color: rgba(255,185,163,0.7) !important;
  box-shadow: 0 0 2px 1px rgba(242,12,12,0.9)  !important; 
}


#s3gt_translate_tooltip_head_pin:not([unpin="true"]),
#s3gt_translate_tooltip_head_fly[is_fly="true"] ,
#s3gt_translate_tooltip_view_source[is_show="true"] ,
#s3gt_translate_tooltip_view_reverse[is_show="true"]  {
	background-color: rgba(85,245,63,0.5) !important;
}




#s3gt_tooltiptxt {
	background: #FDF690;
  border-color: #FF9900;
	color: #000000;
	border-radius: 0px;
}

