    overflow-y:hidden
}
}

@-moz-document regexp(".*lichess\\.org/tournament.*"), regexp(".*lichess\\.org/analysis.*"), regexp(".*lichess\\.org/training.*"), regexp(".*lichess\\.org/insights.*"), regexp(".*lichess\\.org/developers.*") {
