@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("urban-rivals.com") {
  
  
  /*GLOBAL */

  /*Sfondi*/
  /* Normal: http://vignette1.wikia.nocookie.net/urban-rivals/images/1/1a/Background_alternate_1.jpg/revision/latest?cb=20111122194041.jpg */
  /* http://images3.wikia.nocookie.net/__cb20111015111541/urban-rivals/images/9/94/Bg_miss2011.jpg */
  /* http://www.wall321.com/thumbnails/detail/20131124/ubuntu%20ships%20urban%203d%20rivals_www.wall321.com_31.jpg */
  /* http://img853.imageshack.us/img853/1232/wps7.jpg */
  /* http://prntscr.com/95506w */
  /* http://i.imgur.com/tI9I0jY.jpg */
  /* http://i.imgur.com/2szgb0Q.jpg */
  /* http://i.imgur.com/qmffjaN.jpg */
  

  /* nel body potete mettere lo sfondo che vi piace, consiglio quelli qua sopra */
  body {
    background:url("http://i.imgur.com/tI9I0jY.jpg")!important;
      background-repeat: no-repeat !important;
     background-attachment: fixed !important;
     background-size: cover !important;
}


   
 /*--------- new feature 2016 ----------*/ 

.panel-default { 
 color: #E4F20E !important;
   background-color: rgba(192, 105, 19, .5) !important;
  filter: alpha(opacity=70);
  opacity: .7;
}

 /*commenti topic  */ 

.panel {
    color: #F2701E !important;
  /* background-color: rgb(99, 59, 26, .1) !important; */
  background-color: rgba(99, 59, 26, .2) !important;
  
}
  /* info topic */
.topic-list > .media {
    color: #64CECE !important;
}
  .forum-list {
    color: #46AA98; /* *not important !*/
}
  
  /* post it */
  .page-infos {
    opacity: .7;
}
  /* bio eventi */
  fieldset.section-panel {
    background-color: #133601;
    color: #46AA98;
    opacity: .7;
}

 /* collezione pro */
  table {
    color: #06FFDC !important;
}
  

/* 2015 */
  
  /*color text*/
  
  body {
    color: #29FB00 !important;
}
  a {
    color: #DAB207 !important;
}
  label {
    color: #A6CB16 !important;
}
  .text-muted {
    color: #CAFF71 !important;
}
  .text-purple {
    color: #CEACFF !important;
}
  .text-white {
    color: #F3AF57 !important;
}
  
  /* menù player + profilo */
  .dropdown-menu {
    margin: 5px 5px 0px !important;
    background-color: rgb(0, 45, 20)!important;
    border-radius: 10px !important;
}
  .well {
    background-color: rgba(173, 116, 0, 0.3) !important;
    border-radius: 5px !important; 
    border: 1px solid #68D45D !important;
}
  
  /*menù*/
  
  .nav-tabs {
    border-bottom: 1px solid #27722D !important;
}
  
  .nav-tabs.nav-justified > .active > a, .nav-tabs.nav-justified > .active > a:hover, .nav-tabs.nav-justified > .active > a:focus {
    border-bottom-color: rgb(8, 153, 5) !important;
}
.nav-tabs.nav-justified > .active > a, .nav-tabs.nav-justified > .active > a:hover, .nav-tabs.nav-justified > .active > a:focus {
    border: 1px solid rgb(70, 158, 93) !important;
}
  .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    color: #97B30B !important;
    background-color: rgb(0, 0, 0) !important;
    border-color: #DDD #DDD transparent !important;
}
  .nav-tabs.nav-justified > li > a {
    border-bottom: 1px solid rgb(0, 255, 39) !important;
}
  .nav-tabs > li > a {
    color: #A5773B !important;
    background-color: rgb(2, 12, 8) !important;
}
  .nav > li > a:hover, .nav > li > a:focus {
    background-color: rgba(6, 39, 11, 0.8) !important;
}
  
  .dropdown-menu > li > a { 
  background-color: rgba(18, 94, 227, 0.7) !important;
  /* border-radius: 5px !important; */
}
  .bg-warning {
    background-color: rgba(48, 96, 14, 0.44) !important;
}
  
  /*navigazione*/
  
  .navbar-nav > li > .dropdown-menu {
   background: none !important;
     border-radius: 5px !important;
}
  
  .navbar-inverse .dropdown-menu {
    color: #40DE21;
   background-color: rgba(28, 45, 9, 0.77) !important;
     border-radius: 5px !important;
}
   .navbar-inverse .dropdown-menu > li > a:hover,
  .navbar-inverse .dropdown-menu>li>a:focus{
	background-color: rgba(20, 219, 227, 0.7);
	color:yellow !important;
  border-radius: 5px !important;
}
  
 
  
  
  /*COMMUNITY + GLOBAL*/
  
  /*background head*/
  .page-header {
    background-color: rgba(89, 38, 186, 0.2)!important;
}
  
  /*down header*/
  .breadcrumb {
    background-color: rgba(3, 68, 44, 0.4) !important;
    border-radius: 5px !important;
}
  .pagination > li > a, .pagination > li > span {
    color: #FFA319 !important;
    border: 1px solid #17962B !important;
    background-color: rgba(0, 173, 27, 0.3) !important;
    border-radius: 5px !important;
}
  
  /*background table-market */
  table {
    background-color: rgba(0, 255, 102, 0.2) !important;
    border-radius: 5px !important;
    margin-bottom: 1px !important;
    font-size: 75% !important;
}
  .table-hover>tbody>tr:hover{
	background-color: rgba(46, 220, 240, 0.2)!important;
}
  .tab-content {
    background-color: rgba(30, 132, 140, 0.3) !important;
}
  
  
  
  /*comments*/
 /* OLD .panel {
    margin-bottom: 10px !important;
    background-color: rgba(33, 101, 13, 0.6) !important;
} */


  .panel-warning > .panel-heading {
    color: #00DD12 !important;
    border-color: #1B5140 !important;
    background-color: rgba(44, 204, 13, 0.2) !important;
}
 /* OLD  .panel-default > .panel-heading {
     color: #00DD12 !important;
    border-color: #1B5140 !important;
    background-color: rgba(44, 204, 13, 0.2) !important;
  }

*/

  .panel-footer {
    padding: 1px 15px !important;
    background-color: #266307 !important;
    border-top: 1px solid #409613 !important;
}
  .panel-heading {
    border-bottom: 1px solid transparent !important;
    padding: 3px 45px !important;
}
/* OLD  .panel-default {
    border-color: #235720 !important;
      border-radius: 5px !important;
} */

  .panel-warning {
    border-color: #C300FF !important;
      border-radius: 5px !important;
}
  /* comment input*/
  .form-control {
    color: #38FF06 !important;
    background-color: #000 !important;
    border: 1px solid rgb(46, 141, 11) !important;
  }
  .btn-primary {
    color: #FFFC00 !important;
    background-color: #B6631D !important;
    border-color: #2C240A !important;
    border-radius: 5px !important;
}
  
  /*comment counter*/
  .btn-default {
    color: #C6CB18 !important;
    background-color: #2BA80C !important;
    border-color: #15440B !important;
    border-radius: 5px !important;
}
  .btn-xs, .btn-group-xs > .btn {
    border-radius: 3px !important;
}
  
  /*Homepage*/
  /*table n+1*/
  .game-news .media:nth-of-type(2n), #friends-feed .media:nth-of-type(2n):not(.media-date) {
    background-color: #586C22 !important;
    border-top: 1px solid #72AA17 !important;
    border-bottom: 1px solid #467D0B !important;
    background-color: rgba(1, 216, 80, 0.2) !important;
}
  /*player ranking*/
  .table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: rgba(239, 160, 0, 0.2) !important;
}
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 1px solid rgb(144, 155, 76) !important;
}
  .text-success {
    color: #0FF !important;
}
  .text-danger {
    color: #6900FF !important;
}
  
  /*elo ban*/
  .thumbnail {
    border: 1px solid #A58015 !important;
    background-color: rgba(218, 141, 27, 0.51) !important;
    border-radius: 20px !important;
}
  .thumbnail .caption {
    color: rgba(21, 13, 7, 0.7) !important;
}
  .alert-info {
    color: #1F99D5 !important;
}
  
  .alert-info {
    border-color: #00E162 !important;
    color: rgb(31, 153, 213) !important;
    background-color: rgba(16, 29, 74, 0.6) !important;
}
  /*ranking*/
  .pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    color: #CEAB16 !important;
    border-color: #679F17 !important;
    background-color: rgba(44, 204, 13, 0.2) !important;
}
  
  /*market*/
  .alert-warning {
    border-color: #589520 !important;
    color: #8A6D3B !important;
    background-color: rgba(216, 191, 1, 0.2) !important;
    border-radius: 5px !important;
}
  .media {
    padding: 1px !important;
    background-color: rgba(11, 39, 9, 0.5) !important;
    border-radius: 5px !important;
}
  .media:first-child {
    margin-top: 0px !important;
}
  
  .panel-body {
    padding: 5px !important;
}
  .margin-bottom-xs {
    margin-bottom: 0px !important;
}
  .carousel-market .item {
    background-color: rgba(250, 255, 41, 0.26) !important;
}
  .carousel-market, .img-character {
    display: inline-block;
    border: 1px solid rgba(105, 78, 24, 0.81) !important;
    width: 140px !important;
    height: 112px !important;
}
  
 
  
/*history*/
  .table > thead > tr > td.active, .table > tbody > tr > td.active, .table > tfoot > tr > td.active, .table > thead > tr > th.active, .table > tbody > tr > th.active, .table > tfoot > tr > th.active, .table > thead > tr.active > td, .table > tbody > tr.active > td, .table > tfoot > tr.active > td, .table > thead > tr.active > th, .table > tbody > tr.active > th, .table > tfoot > tr.active > th {
    background-color: rgba(15, 0, 251, 0.42) !important;
}
  .table-condensed > thead > tr > th, .table-condensed > tbody > tr > th, .table-condensed > tfoot > tr > th, .table-condensed > thead > tr > td, .table-condensed > tbody > tr > td, .table-condensed > tfoot > tr > td {
    padding: 5px !important;
}
  
   /*setting yes/not*/
 
   .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
    color: rgb(249, 123, 0) !important;
    background-color: rgba(84, 255, 0, 0.52) !important;
    border-radius: 5px !important; 
}
  
  /*messaggi privati*/
  
  .messages-container {
    background-color: transparent !important;
}
   /* OLD .panel > .list-group .list-group-item, .panel > .panel-collapse > .list-group .list-group-item {
    background-color: rgba(0, 0, 0, 0.82) !important;
} */
 
}
