    background-color: #ff0000;
    border-radius: 3px;
    height: 20px;
    margin: 0 5px 0 0;
    padding: 4px;
    vertical-align: middle;
    width: 20px;
}
img.icon.dark {
    background-color: #111111 !important;
}
img.icon.large {
    height: 28px !important;
    width: 28px !important;
}
img.badge {
    border-radius: 3px;
    height: 16px;
    padding: 2px;
    vertical-align: text-top;
    width: 16px;
}
img.img-left {
    float: left;
    margin-right: 5px;
}
img.img-right {
    float: right;
    margin-right: 5px;
}
img.size-down {
    height: 100px !important;
    width: 100px !important;
}
img.frame {
    border: 1px solid #111111;
}
img.rounded {
    border-radius: 5px;
}
img.rounded15px {
    border-radius: 15px;
}
img.bg {
    background-color: #111111;
    padding: 3px;
}
img.fade-out {
    box-shadow: 0 0 10px 5px #111111;
}
img.fade-out-hard {
    box-shadow: 0 0 15px 10px #000000;
}
.note {
    color: #f89406;
    font-size: 85%;
}
.monospace {
    font-family: "Inconsolata" !important;
    font-size: 16px !important;
}
.font-smaller {
    font-size: 85%;
}
.font-bigger {
    font-size: 130%;
}
.bold, b, strong {
    font-family: "Roboto Medium";
    font-weight: 500;
}
.italic, i, em {
    font-style: italic;
}
.underline, ins {
    text-decoration: underline;
}
.cross-out, s, del {
    text-decoration: line-through;
}
.left {
    text-align: left;
}
.right {
    text-align: right;
}
.center {
    text-align: center;
}
.mv10 {
    margin: 10px 0;
}
.pv10 {
    padding: 10px 0;
}
.mh10 {
    margin: 0 10px;
}
.ph10 {
    padding: 0 10px;
}
.mb10 {
    margin: 0 0 10px;
}
.pb10 {
    padding: 0 0 10px;
}
.w100i {
    box-sizing: border-box;
    max-width: 100% !important;
    width: 100% !important;
}
.h100i {
    box-sizing: border-box;
    height: 100% !important;
    max-height: 100% !important;
}
.hr {
    border-bottom: 1px solid #222222;
    margin-bottom: 10px;
    padding-bottom: 10px;
}
.hr-light {
    border-bottom: 1px solid #333333;
    margin-bottom: 10px;
    padding-bottom: 10px;
}
@media all and (min-width: 1024px) {
.container {
    width: 98%;
}
header .mobile-only {
    display: none;
}
header #logo {
    background-position: left center;
    display: inline;
    float: left;
}
header nav > ul > li {
    display: inline-block;
}
header nav > ul > li > a {
    display: inline-block;
    font-family: "Rajdhani";
    font-size: 20px;
    padding: 18px 15px;
    transition: none 0s ease 0s ;
}
header nav > ul > li:hover > a {
    color: #ffffff;
}
header nav > ul > li:hover > a + ul {
    display: block;
}
header nav > ul > li:hover > a + ul::before {
    border-bottom: 15px solid #c31a1a;
    border-left: 15px solid transparent;
    border-right: 15px solid transparent;
    content: " ";
    display: block;
    height: 0;
    margin: -25px 0 0;
    width: 0;
}
header nav > ul > li > ul {
    background-color: #c31a1a;
    border-radius: 3px;
    box-shadow: 0 1px 10px 1px #111111;
    display: none;
    padding: 15px 15px 5px;
    position: absolute;
}
header nav > ul > li > ul > li > a {
    border-bottom: 1px solid transparent;
    color: #ffffff;
    display: block;
    font-family: "Roboto Medium";
    padding: 3px 0;
}
header nav > ul > li > ul > li > a:hover {
    border-bottom: 1px solid #ffffff;
}
header nav > ul > li > ul > li > a.emphasis {
    color: #ffffff;
    font-size: 17px;
}
footer {
    background-position: center center;
    padding: 20px 0;
}
}
@media all and (min-width: 1024px) and (max-width: 1139px) {
#main {
    font-size: 90%;
}
#main #sidebar #actions a {
    padding: 3px 0;
}
#main input, #main select, #main textarea {
    font-size: 13px !important;
    padding: 3px !important;
}
#main input[type="submit"], #main input[type="button"], #main input[type="reset"] {
    font-size: 13px !important;
    padding: 3px 8px !important;
}
#main img.icon {
    height: 16px !important;
    width: 16px !important;
}
#main img.icon.large {
    height: 20px !important;
    width: 20px !important;
}
}
@media all and (max-width: 1023px) {
.container {
    width: 100%;
}
header #logo {
    background-position: center center;
    display: block;
    float: none;
    margin: 0 auto;
}
header #search-form {
    display: none;
}
header label.mobile-only {
    background-position: left center;
    background-repeat: no-repeat;
    background-size: 30px 30px;
    border-radius: 3px;
    cursor: pointer;
    height: 30px;
    left: 10px;
    position: absolute;
    top: 15px;
    width: 30px;
}
header input.mobile-only[type="checkbox"] {
    display: none;
}
header input.mobile-only[type="checkbox"]:checked + nav {
    display: block;
}
header nav {
    display: none;
}
header nav > ul {
    background-color: #333333;
    display: block;
    max-width: 70%;
}
header nav > ul > li {
    display: block;
}
header nav > ul > li > a {
    display: inline-block;
    font-family: "Rajdhani";
    font-size: 18px;
    padding: 10px;
}
header nav > ul > li:hover a {
    color: #ffffff !important;
}
header nav > ul > li:hover a + ul {
    display: block;
}
header nav > ul > li > ul {
    background-color: #222222;
    display: none;
    padding: 5px 0;
}
header nav > ul > li > ul > li a {
    display: inline-block;
    padding: 5px 10px 5px 30px;
}
header nav > ul > li > ul > li a:hover {
    background-color: #c31a1a;
}
#main #content, #sidebar {
    padding: 0 10px;
    width: 100%;
}
#sidebar #actions a {
    padding: 5px 0;
}
#profile-wrapper #avatar, #profile-wrapper #info {
    display: block;
    width: 100%;
}
footer {
    background-position: center top;
    padding: 50px 0 0;
}
footer .col.w50 {
    display: block;
    margin: 0 0 20px;
    min-width: 100%;
    text-align: center;
}
}

}
