css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://userstyles.org), url-prefix(http://www.userstyles.org) , url-prefix("https://userstyles.org") {
html,body>h1,h2,h3,form {margin:0!important;}
body {background-attachment:fixed!important;padding:0!important;margin:-4px auto 0 auto!important;width:1000px;color:#7594c1!important;background-color:#fff!important}
h1,h2,h3 {clear:both!important;color:#7594c1!important;font-size:180%}
ul {padding:0!important}
ul, li, p {font-size:12px!important;}
li {list-style:none!important;}
p {padding:5px!important;margin:0!important;color:#7594c1!important;}
body > ul#style-list {border:0!important;}
body > p, .screenshot-box, #screenshots {text-align:center!important}

a, a:visited {color: #7594c1!important;margin:1px!important;text-decoration:none!important}
a:hover,  a:active {color:#426395 !important;text-decoration:underline!important;}
a.unranked, a.unranked:hover {padding:2px 2px 2px 20px;color:#426395!important;background-image:url(http://userstyles.org/images/star_0.png)!important;background-position:0 0!important;background-repeat:no-repeat!important;max-width:200px!important;}
a.poor-rank, a.poor-rank:hover {display:none!important;}
a.medium-rank, a.medium-rank:hover {padding:2px 2px 2px 20px;color:#426395!important;background-image:url(http://userstyles.org/images/star_05.png)!important;background-position:0 0!important;background-repeat:no-repeat!important;max-width:200px!important;}
a.good-rank, a.good-rank:hover {padding:2px 2px 2px 20px;color:#426395!important;background-image:url(http://userstyles.org/images/star_1.png)!important;background-position:0 0!important;background-repeat:no-repeat!important;max-width:200px!important;}

#logo {margin:6px 0 15px 20px !important;background:none!important;padding:0 8px 4px 8px !important;}
#logo a {text-transform:capitalize!important;font-family:serif!important;color:#7594c1!important;text-decoration:none!important}

#search {padding:4px 20px 0 0!important}
#search-submit {border:0!important;padding:1px!important}

#navigation {margin:19px 0 0 0!important;padding:0 10px 0 0!important;}
#navigation .current-page {border-top: 1px solid #7594c1!important;border-left: 1px solid #7594c1!important;border-right: 1px solid #7594c1!important;}
#navigation a:hover {border:1px solid #7594c1!important;border-bottom:0!important;color:#426395!important;background:#fff!important;text-decoration:none!important}
#navigation a {color: #fff!important;background:#7594c1!important;}

#header-bar {background:#E0E7F1!important;border:1px solid #7594c1!important;margin:0!important;-moz-border-radius:8px!important;}
#main-text {border:1px solid #7594c1!important;margin:0!important;padding:0 10px 0 10px!important;clear:both!important;background:#E0E7F1!important;-moz-border-radius:8px!important;}
#main-text p {font-size:14px!important}
#main-text a {text-decoration:underline!important;color:#426395!important;}
#sidebar {width:100%!important;margin:0!important;padding:0!important;float:left!important;background:#fff!important;-moz-border-radius:8px!important;}
#sidebar * :hover {list-style:disc!important;text-decoration:none!important}
#sidebar h2 {-moz-border-radius-topleft:5px!important;-moz-border-radius-topright:5px!important;margin:10px 0 0 0!important;padding:4px!important;background:#7594c1!important;text-align:center!important;}
#sidebar h2 a {color:#fff!important;}
#sidebar ul {border:1px solid #7594c1!important;-moz-column-count:3!important;background:#E0E7F1;padding:5px!important;-moz-border-radius-bottomleft:5px!important;-moz-border-radius-bottomright:5px!important;}
#sidebar ul li {margin:0 0 0 15px!important;padding:0!important;text-align:left!important;text-indent:-4px;border-bottom:1px dashed #E0E7F1!important}
#sidebar ul li a {white-space:nowrap;margin:0!important;padding:0!important;}
.all-link, .post-your-style {width:auto!important;text-align:center!important;padding:10px 0 0 0}

#account-link::before {font-size:11px!important;content: "\27A1"!important;padding:1px 2px 1px 3px!important;color:#fff!important;background:#7594c1!important;-moz-border-radius:10px}
#miscellaneous {color:#fff !important;text-align:center!important;margin:5px 0 5px 0!important}
#account-link a {color:#426395!important;text-decoration:none!important;}
#stylish-install {color:#426395!important;}
#style-list,#style-list ul {border:1px solid #7594c1!important;-moz-column-count: 2 !important;padding:6px!important;clear:both!important;margin:0!important}
#style-list li {font-size:15px!important;margin:7px 0 7px 0!important;clear:both!important;white-space:nowrap!important}
#style-show {background:#E0E7F1!important;margin:0!important;text-align:left!important;-moz-border-radius:11px!important;border:1px solid #7594c1!important;padding:8px!important;}
#style-meta {width:140px!important;font-size:11px!important;float:right!important;text-align:center!important;background:#fff!important;-moz-border-radius:11px!important;color:#426395!important;padding:8px!important;margin-top:5px!important;}
#style-show > h3 {text-align:center!important;background:#7594c1!important;color:#fff!important;padding:3px!important;-moz-border-radius-topleft:6px!important;-moz-border-radius-topright:6px!important;}
#show-button {margin:0 0 0 456px!important}
#style-show > #long-description {min-height:110px!important}
#style-show > h2, #style-show > ul {text-align:center!important}
#style-show > ul > li {line-height:1.7em!important}
#view-code {line-height:0.95em!important;background:#fff!important;color:#000!important;border: 1px dashed #7594c1!important;margin:5px!important;max-height:400px!important}
#stylish-code {font-size:11px!important;font-family:Monospace!important;}
#screenshots img {border:4px dashed #fff!important}
#long-description p a::before {font-size:14px!important;content: "\27A1"!important;margin:3px!important;padding:1px!important;color:#fff!important;background:#7594c1!important;-moz-border-radius:4px}
#long-description a {font-weight:bold!important;text-decoration:none!important}

.screenshot-box, #screenshots {background:#fff!important;}
.screenshot-table {clear:both;margin:0 auto 0 auto!important;}
.category-meta, h1, form#category-search {text-align:center!important;margin:0!important}
.category-style-top {width:100%!important;}
.category-style-top ul li {line-height:1.7em!important;clear:both!important}
.category-style-top ul li:hover, .category-meta ul li:hover {list-style:disc!important;color:#426395!important;}
.category-style-top ul, 
.category-meta ul {background:#E0E7F1!important;border:1px solid #7594c1!important;-moz-column-count:2!important;-moz-border-radius-bottomleft:10px!important;-moz-border-radius-bottomright:10px!important;margin:0 0 5px 0!important;padding:2px!important;text-align:center!important;}
.category-style-top h2, .category-meta h2 {color:#fff!important;font-size:15px!important;background:#7594c1!important;padding:4px!important;
margin:5px 0 0 0!important;text-align:center!important;-moz-border-radius-topleft:10px!important;-moz-border-radius-topright:10px!important;}

#reviews {border:1px solid #7594c1!important;margin:0!important;padding:10px!important;background:#fff!important;-moz-border-radius-bottomleft:6px!important;-moz-border-radius-bottomright:6px!important;}
.review-container {margin:0!important;padding:0!important;}
.review-container h3 {padding:0!important;}
.review-container blockquote {padding:0 0 0 0!important;margin:0 0 10px 0!important}
.form-controls {border:1px solid #7594c1!important;-moz-border-radius:11px!important;background:#E0E7F1!important;margin:4px 0 4px 0!important;padding:10px!important}
.form-controls th {padding:5px!important;font-size:12px}

a.edit-link, #control-panel a {text-transform: uppercase;clear:both!important;font-size:9px!important;color:#600!important}

span.stars {background:#fff!important;}

.simple-link-ads, #simple-link-ads {display:none!important;}

img[src*="/images/feed-icon-16x16.png"] {height:13px!important}

}

@-moz-document url-prefix(http://userstyles.org/users) , url-prefix("https://userstyles.org/users") {
body ul:not([id]) * {font-family:serif!important;font-size:13px}
body ul:not([id]) {-moz-column-count:3!important;padding:5px!important;border:1px solid #7594c1!important;-moz-border-radius-bottomleft:6px!important;-moz-border-radius-bottomright:6px!important;background:#E0E7F1!important;text-align:center!important;margin:0 auto 10px auto!important;}
body ul:not([id]) li:hover {list-style:square!important;color:#7594c1!important}
body > h1 {padding:3px!important;font-size:18px!important;-moz-border-radius-topleft:6px!important;-moz-border-radius-topright:6px!important;background:#7594c1!important;text-align:center!important;margin:10px auto 0 auto!important;color:#fff!important}
body ul:not([id]) li a {white-space:nowrap!important}
}
