css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rarbg.com"), domain("rarbg.to") {

.lista-rounded, .lista, input, textarea,
.lista2 table tr td, .scrollspycomments div div
{
background:#121315 !important;
}
#pager_links b, #pager_links a:hover,
#searchTorrent, td.lista2, .searchTorrent,
.header2, .btn
{
background:#262A35 !important;
}
.btn-primary, .selected
{
background:#555555 !important;
}
#pager_links a, textarea,
.lista-rounded, .lista2, 
#description div div
{
border:1px #851A23 solid !important;
}
table.lista2t tr.lista2 td, table.lista2t,
table.lista tbody tr td.lista
{
border-bottom:1px #851A23 solid !important;
}
table.lista2t tr.lista2 td, table.lista2t, 
.td.header6, td.header6
{
border-right:1px #851A23 solid !important;
}
table.lista2t
{
border-left:1px #851A23 solid !important;
}
#description div div
{
background:#748BBC !important;
}
.lista, font, .lista span, .lista a, .block,
#searchTorrent a, input, p, .lista2, .black,
.searchTorrent div, .lista td, .btn,
textarea, .ncomments_head, .ncomments_body,
.ncomments_user, .ncomments_votes, .selected a,
.ncomments_date, #pager_links a, #pager_links b
{
color:#CEAE48 !important;
}
}
