css:
@-moz-document domain("feedly.com") {
  /* Remove stuff */
  .xlarge-only, #integrationstab_header, #librarytab_header, .button-share-to-team, button[title="Copy to Team"] {
    display: none !important;
  }
  /* Backgrounds */
  #addContentPlaceholderFX .button-icon-left, .menu, .item-with-visuals, .headerInfo-expanded-scroller, .popover, div.modal.small, .u100Header-banner, #feedlyDialogFramePart, .feed-header, #formActions, .ConfirmDialog {
    background-color: #212121 !important;
  }
  #searchBarFX, #feedlyTabs, .sliderContainer, #headerBarFX, .subscriptionContentsHolder, .u0:hover, .edition, .header.sticky, .headerInfo, #headerBarFX header, #feedlyTabsHolder, #floatingBar, .floatingBar, .modal-notifications, .fixed-bar, .organize thead, .BarMetric--chart, .form.add-url {
    background-color: #303030 !important;
  }
  #mainBarFX, .floatingEntryScroller, .entryholder, #feedlyFrame, #feedlyPageHolderFX, body, .selected:hover {
    background-color: #444242 !important;
  }
  /* Icons */
  .feedly-logo, .item .icon-fx-light-gear-ios-md-black, .icon-fx-rss-ios-sm-black, .icon-fx-google-plus-ios-sm-black, .icon-fx-laurel-ios-sm-black, .icon-fx-add-ios-sm-black, .icon-fx-opml-ios-sm-black, .icon-fx-star-save-ios-md-black:not(.dark), .icon-fx-star-ios-sm-black, .icon-fx-check-ios-sm-black, /* More options for feed dropdown menu  */
  .item .icon-fx-rename-ios-sm-black, .item .icon-fx-gear-ios-sm-black, .icon-fx-organize-ios-sm-black, /* Add content page */
  .item .icon-fx-rss-ios-md-black, .item .icon-fx-google-plus-ios-md-black, /* Organizer */
  .icon-fx-arrowhead-down-ios-sm-black, div.actions-container>button.button-icon-only, header>div>button.button-icon-only, .organize--toolbar-actions button i, /* Article view header */
  .headerInfo-expanded-img:not(.icon-fx-save--active-ios-md), /* Action toolbar */
  .right-col .mark-as-read-button, .right-col .icon-fx-arrowhead-down-ios-sm-black, .right-col .icon-toolbar-refresh-secondary, .right-col .icon-fx-cards-layout, .right-col .icon-fx-magazine-layout, .right-col .icon-fx-title-only-layout, .right-col .icon-fx-more-ios-md-black, .right-col .icon-fx-full-article-layout, /* Magazine view */
  .content>button.mark-as-read, .content>button.hide, .content>button:not(.saved).save-for-later, /* Title only view */
  .entry>button:not(.saved).save-for-later/* for RTL languages */
  , .entry .icon-fx-cross-ios-sm-black {
    filter: invert(100%) !important;
    -webkit-filter: invert(100%) !important;
  }
  /* Fix background color for inverted icons */
  .save-for-later:hover, .fx div .button.primary:hover {
    background-color: transparent !important;
  }
  .icon-fx-save--active-ios-md:hover {
    background-color: #111 !important;
  }
  /* left and right arrows in article mode */
  .slideBumper {
    background: #E0E0E0 !important;
    border-radius: 50% !important;
  }
  /* Text */
  h1, h2, .heading, a:hover, .item-header, .editionInfo, .read a.fx-button {
    color: #EEE !important;
  }
  h3, h4, .title, a, .h2Link, label, .theme-panel-label, .subscription-status, .card-header {
    color: #DDD !important;
  }
  .summary, p, .subscriptionContentsTitle, .item, td, section, div, #filterByCollection, #filterByActivity, .organize thead span, .dollarCents, .MenuItem-kXIozM {
    color: #BBB !important;
  }
  .metadata, .engagement, .action, .read a, span.label {
    color: #AAA !important;
  }
  .read div, .read span {
    color: #888 !important;
  }
  input::placeholder {
    color: #212121 !important;
  }
  /* Unread count next to the check mark, color already inverted so need different color */
  .mark-as-read-button span {
    color: #353535 !important;
  }
  a {
    font-weight: bold !important;
  }
  /* Dark backround reorganize selected sources */
  .menu.m-b-1 {
    border-radius: 4px !important;
  }
  /* Sidebar items */
  .header:not(.row), .feedIndex, .moreHandle {
    transition-duration: .25s !important;
    border-radius: 3px !important;
    padding-right: 6px !important;
  }
  .header:hover:not(.row), .feedIndex:hover {
    background: #555 !important;
    margin-left: 3px !important;
  }
  .moreHandle:hover {
    background: #555 !important;
  }
  /* Move sidebar scrollbar to the left */
  #feedlyTabs {
    direction: rtl !important;
  }
  #feedlyTabs>div {
    direction: ltr !important;
  }
  /* Fix width in magazine view */
  .col-xl-9 {
    width: 100% !important;
  }
  .fx .entry.u4 {
    max-width: none !important;
  }
  /* Buttons */
  .giant-mark-as-read, #feedlyTabsUnpin, .visitWebsiteButton, .small.pro.primary, .follow, div.empty-state-actions>button.primary {
    background: #43a047 !important;
    color: #EEE !important;
    transition-duration: .4s !important;
    box-shadow: none !important;
  }
  .danger.primary, #feedlyPageFX .card {
    color: #EEE !important;
    transition-duration: .4s !important;
    box-shadow: none !important;
  }
  .giant-mark-as-read:hover, #feedlyTabsUnpin:hover, .visitWebsiteButton:hover, .small.pro.primary:hover, .follow:hover, div.empty-state-actions>button.primary:hover {
    background: #2e7d32 !important;
  }
  /* Secondary (gray) buttons */
  button.secondary:not(.button-icon-only):not(.button-icon-left), button.following-feed {
    border: 1px solid #777 !important;
  }
  /* Disable boarder for for profile icon/letter */
  .profile-bubble .button-dropdown button.secondary, #feedlyPageFX button.giant-mark-as-read {
    border: none !important;
  }
  /* Preferences buttons and chips */
  #feedlyPageFX .card, .chip.small, span.chip, .price-table-column, .stripe-payment-modal {
    box-shadow: none !important;
    background-color: #555 !important;
    border-top: solid 2px #2bb24c !important;
    color: #999 !important;
  }
  #feedlyPageFX .card:hover, .chip.small:hover {
    color: #DDD !important;
    background: #656565 !important;
  }
  /* Caught up image */
  div.empty-state.is-medium>img[alt="Well Done! illustration"] {
    height: 264px !important;
  }
  /* Title only view */
  .read.u0 {
    margin-bottom: 1.5px !important;
    border-bottom: 1px solid #555 !important;
    border-radius: 6px !important;
  }
  /* Article view, add dash between articles */
  div.inlineFrame.read.u100 {
    border-bottom: 3px dotted #BBB !important;
  }
  /* Organize sources page */
  /* Remove gradient */
  .fx .organize--feed.feed .item-content::after, .fx .organize--cell-actions {
    background: none !important;
  }
  /* Recycle bin button */
  button.organize--hover-button {
    background-color: #DDD !important;
  }
  button.organize--hover-button:hover {
    background-color: #EEE !important;
  }
  /* Index page */
  #feedlyPage {
    background-color: #303030 !important;
  }
  #feedlyPage>#mainArea {
    border-bottom: 1px solid #BBB !important;
    background-color: #212121 !important;
  }
  #feedlyPage>div {
    background: none !important;
    padding-left: 34px !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    padding-top: 12px !important;
  }
  /* NO BORDERS */
  * {
    border: none !important;
  }
}
