css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
 
  .tweet-text {color: black !important;}
  .js-relative-timestamp,
  ._timestamp
	{color:maroon !important;}
  
  .QuoteTweet-screenname,
  .twitter-atreply b, .twitter-atreply s,
  .ProfileHeaderCard-screennameLink.u-linkComplex,
  .username, .username s {color: green !important;}
  
  .flex-module-header h3,
  .trend-location,
  .WhoToFollow-title {color: black !important;}
  
  .js-retweet-text .js-user-profile-link b,
  .stream-item-activity-line-notification,
  .social-proof
	{color: navy !important;}
  
  .metadata.social-context,
  .js-retweet-text, .js-display-url {color: #ae2268 !important;}

  a.list-link {color: #4d4dff !important;}
  a.pretty-link {color: #b9417d !important;}
  .trend-item-stats {color: green !important;}
  a.twitter-hashtag * {color: blue !important;}
  
  .Footer-item {color: brown !important;}
  .Footer-item .Footer-link {color: blue !important;}

}
