css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("alliancesatwar.com") {
html {
    background: url('http://i.imgur.com/shelKVt.jpg')no-repeat center center fixed;
    -webkit-background-size: cover;
    /*moz-background-size: cover;*/
    -o-background-size: cover;
    background-size: cover;
}
body {
    background: none;
    background-image: url('http://i.imgur.com/mF2O7Fm.jpg');
    background-repeat: repeat-x;
    background-attachment: fixed;
}
#container {
    padding-top: 40px;
	width: 1200px;
}

header h1 {
	left: 0;
	width: 220px;
    padding: 0 10px;
	border-radius: 5px;
	background-color: black;
	text-align: center;
}
header h1 a img {
    width: 150px;
    height: 50px;
}
header.top {
    width: 200px;
    background: none;
	
}
#gameStats {
    background-color: rgba(0, 0, 0, 0.5);
    left: 0;
    top: 75px;
    border-radius: 3px;
}
a.noScript {
	top: 78px;
	left: 220px;
}
#analogClock {
	top: auto;
	position: fixed;
	border-radius: 40px;
	border: 1px solid steelblue;
	background-color: steelblue;
	bottom: 45px; 
	left: 50%;
	margin-left : -450px;
}
.clock {
    color: #fff;
}
#tribeStats {
    left: 0;
    padding: 10px;
    width: 220px;
    background-color: rgba(0, 0, 0, 0.5);
    top: 165px;
}
#menu {
    z-index: 5;
    position: fixed;
    background-image: url('http://i.imgur.com/mF2O7Fm.jpg');
    background-repeat: repeat-x;
    top: 0;
    width: 960px;
    margin: auto;
    padding-top: 4px;
	margin-left: 250px;
    height: 25px;
}
#menu .first ul {
    background: rgba(0, 0, 0, 0.7);
}
#menu li {
    height: 25px;
	width: 100%;
}
#menu a {
    color: #fff;
}
#menu > aside input {
	border-radius: 5px;
	background: none;
	background-color: #333;
	border : 1px solid #666;
}
#menu > aside input:focus{
	border : 1px solid steelblue;
}
#menu > aside > ul {
	background: none; 
	background: #222;
}
#menu > aside > ul:focus {
	background-color: #333;
}
#content {
    top: 40px;
    width: 960px;
    margin-left: 250px;
    background: rgba(0, 0, 0, 0.7);
    border-radius: 3px;
}
#content nav:before {
	content: "Navigation: ";
	font-size: 12px;
	font-weight: bold;
	text-decoration: underline;
}
#content nav {
	height: auto;
	background-color: rgba(0, 0, 0, 0.5);
	border-radius: 3px;
	width: 220px;
	position: fixed; 
	left: 50%;
	margin-left : -600px;
	top: 265px;
	padding: 10px;
}
#content nav a {
    border: none;
}
#content nav ul {
	width: 100%;
    list-style: none;
    margin: auto 0;
    padding: 0;
    margin-bottom: 5px;
}
#content nav ul li {
    display: list-item !important;
    width: 100%;
	clear:both;
}
#content nav ul a {
	padding: 4px 0;
    background: none;
    border-radius: 3px;
}
#content nav ul a:hover {
	background: none;
	padding: 4px 0;
	border: 0;
	display: contents;
}
#content nav ul {
	left: 0;
	width: 120px;
}
.holder,
.tiny,
.small,
.mediums,
.medium,
.big {
    background: rgba(0, 0, 0, 0.3);
}
table {
    background: rgba(0, 0, 0, 0.3);
}
table caption,
thead {
    background: rgba(0, 0, 0, 0.3);
    color: #fff;
}
thead th {
    color: #fff;
}
footer {
    background: rgba(0, 0, 0, 0.5);
    border-radius: 3px;
    padding: 10px;
    width: 220px;
    bottom: 32px;
    margin-left: 5px;
    height: 90px
}
footer.top {
    margin: 0;
}
footer nav {
    position: static;
    width: 220px;
}
footer nav li {
    display: list-item !important;
    padding-top: 5px;
}
footer p {
    padding-left: 5px;
    top: 80px;
}
}
