css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
	/*
 	 *	HN+ for Chrome v1.5
	 *	by @jarques
	 *  modified by @thinkaliker for use with Stylish
	 */

	body {
		background: #fff;
		font-family: Helvetica !important;
		margin: 0;
	}
	
	
	#hnmain {
		margin: 10px auto;
		width: 980px;
	}
	
	table, td {
		background: none !important;
	}
	
	table tr {
		position: relative;
	}
	
	a:link  { color: #202020 !important; }
	a:hover { color: #dd4b39 !important; }
	
	input[type="text"] {
		border: 1px solid #c6cfd6;
		font-size: 12px !important;
		padding: 4px;
	}
		
	textarea {
		font-family: "Helvetica" !important;
		border: 1px solid #c6cfd6 !important;
		padding: 4px !important;
		font-size: 12px !important;
		width: 100%;
	}
	
	input[type="submit"] {
		background: #4D90FE;
		border: 1px solid #4079D6;
		border-radius: 2px;
		color: white;
		font-family: "Helvetica";
		font-size: 12px;
		padding: 5px 10px;
		text-transform: capitalize;
	}
	
	input[type="submit"]:hover {
		background: #3C89ED;
	}
	
	input[type="submit"]:active {
		font-family: "Helvetica";
		background: #2b78DC;
	}

	.pagetop, .pagetop a {
			color: #000000 !important;
	}
	
	.pagetop b a {
		color: #000000 !important;
		padding-right: 20px;
		text-transform: lowercase;
	}
		
	.rank {
		font-size: 12px !important;
	}
	
	.subtext {
		color: #2F2F2F;
		font-size: 10px;
		padding-bottom: 15px;
	}
	
	.subtext a {
		color: #2F2F2F !important;
	}
	
	.sitebit {
		color: #AAA !important;
	}

	.score {
		color: #0099CC !important;
	}

	.default {
		padding-bottom: 10px;
		border-bottom: 1px dotted #d8d8d8;
	}
	
	.comment {
		color: #373736;
		font-size: 13px !important;
		line-height: 20px;
	}
	
	.comment code {
		color: #373736;
		font-size: 13px !important;
		line-height: 20px;
	}
	
	.comment p {
		font-size: 13px !important;
		margin-top: 15px !important;
		line-height: 20px;
	}
	
	form textarea {
		background: #fff;
		border: 1px solid #c9c9c7;
		font-size: 14px;
		padding: 10px;
		height:80px;
		overflow: auto;
	}	
	
	.yclinks {
		color: #AAA !important;
		font-size: 10px;
	}
	
	.yclinks a {
		color: #b8b8b8 !important;
	}
	
	input[name="q"] {
		border-radius: 2px;
		width: 270px;
	}
}
