css:
@-moz-document regexp("http://.*\.theverge\.com/.*") {

  /* Remove that "unread" count */
  #js-new-articles-toggle {
    display: none !important;
  }
  
  /* Fix the width of the (removed) sidebar and comments. */
  /* This happens since we removed the sidebar. */
  .m-entry__body .m-entry__sidebar {
    width: 0 !important;
  }
  .m-article__comments-section .m-article__comments__sidebar, .m-article__comments-section .m-article__comments__content {
    display: block !important;
  }
  
  /* Make image in article go 100% width, that's way better. */
  .m-article__entry img, .m-stream__header img {
    width: 100%;
  }
  
}
