css:
@-moz-document domain("mail.com"), domain("navigator-lxa.mail.com") {

/*--------NO-ADS------------------------------*/

#content-sky,
#superbanner,
.adi--4-1,
.ad.base-page__ad,
.ad-container,
.ad-content,
.ad-sky,
.native-content-box-ad--preview-right,
.native-content-box-ad--sticky,
.js-component.native-content-box-ad--preview-bottom.native-content-box-ad,
.js-component.native-content-box-ad--no-preview.native-content-box-ad,
 tr+.js-component,
[class*="ad adb"] {
  display: none !important;
}

.panel-mail-display-table-mail-horizontal #maillist.has-sticky-ad>.scroll.has-paging>.scroll-pane {
    bottom: 28px;
}

.panel-mail-display-table-mail-default #maillist.has-sticky-ad>.scroll.has-paging>.scroll-pane {
    bottom: 0px;
}


[class*="content"],
[class*="section-container"],
[class*="mail-display-wrapper"] > *,
.no-sky.can-have-adb .section-1 > .section-container {
  margin-right: 0px!important;
  right: 0px!important;
  margin-bottom: 0px!important;
  bottom: 0px!important;
}

[class="grid-container grid-left"] > *,
[class*="app-contents"] *:not(img):not(button):not(pos-spinner):not(cloud-app):not([class^="pos-"]),
#regions {
  margin-right: 0px!important;
  right: 0px!important;
  margin-bottom: 0px!important;
  bottom: 0px!important;
}

#desk [class="component"],
[data-component="NativeContentBoxAd"],
pos-third-party-app,
[class="grid-container grid-right"] {
  display: none!important;
  max-width: 0%!important;
}

/*[[AdsCols]]*/


/*[[NoAdsCols]]*/


/*[[NoAds]]*/

}

style_setting_options.value:
/*--------COLORS-----------------------------*/

[id^="editor-"],
[class="cloud-action-bar__list-item"] :not([class*="organizer"]),
[class="atl-actions-menu__hidden atl-actions-menu__hidden--open"] :not([class*="organizer"]),
cloud-tree-node :not([class*="organizer"]),
:not(:empty):not(i):not(img):not(input):not(svg):not(audio):not(video):not(button):not(pos-brand-logo):not(pos-icon-item):not(pos-spinner):not([class*="organizer"]):not([class="gui-toggle-control"]):not([class*="button-container"]):not([class*="atl-app-contents-wrapper"]):not([class*="customhtml"]):not([class*="icon"]):not([class*="lightbox"]):not([class*="m-button"]):not([class*="ng-binding ng-scope"]):not([class*="ng-isolate-scope active"]):not([class*="player"]):not([class*="prite"]):not([class*="stars"]):not([class*="video"]):not([class^="atl-actions-menu"]):not([class^="atl-navbar"]):not([class^="form-submit"]):not([class^="id"]):not([id="id123"]):not([class^="pos-"]):not([class="compose-header_control"]):not([class="control controlSec"]):not([class="rating"]):not([id*="lbImage"]):not([id*="player"]):not([id*="prite"]):not([id*="stars"]):not([id*="video"]):not([id="rating"]):not([data-component="ButtonGroup"]):not([data-id="selectOne"]):not([onclick*="open"]):not([class="themewrapper"]):not([class="header-bar"])>*:not([class="btn btn-block login-submit"]):not([class^="atl-"]):not([class*="search"]):not([class="button cta  mailcom "]):not([class*="icon"]):not([class*="checkbox"]):not([class*="organizer"]) {
  color: #EEEEEE !important;
  background-color: /*[[BgColor]]*/;
}


[id="toolbar"],
[class*="fc-toolbar fc-header-toolbar"]:not([class*="organizer"]),
[class="actionarea action-head"] :not([class="hero"]):not([class*="organizer"])
{
    background: none /*[[BgColor]]*/ ! important;
}



html,
body *,
table,
tbody *,
html,#mail-instant-reply,#toolbar :not([class*="organizer"]),aside,
.splitpanel-settings-content_wrapper :not([class*="icon"]):not([class*="search"]):not([class*="organizer"]),
cloud-tree-node :not([class*="search"]):not([class*="organizer"]),
.container.js-container :not(svg):not([class*="icon"]):not([class*="search"]):not([class*="organizer"]),
[class="actionarea action-head"] :not([class*="search"]):not([class*="organizer"]),
.cke_top.cke_reset_all,
.cke_toolbox
{
    background-color: /*[[BgColor]]*/ !important;
}


.cke_button:hover *,
[class*="m-button"],
.pos-button:not(:hover),
div[class^="nav-item"]:hover *,
[class="atl-actions-menu__hidden atl-actions-menu__hidden--open"],
div.panel.panel-search *,
.cke_toolbar *
{
    color: #EEEEEE !important;
    background-color: /*[[ScrThumb]]*/ !important; 
    border-color: /*[[ScrThumb]]*/ !important; 
}


/*[class*="button"] */
button,
[type="button"]
{
    color: #EEEEEE !important;
    background: none /*[[ScrThumb]]*/ ! important;
    background-color: transparent !important; 
    border-color: /*[[ScrThumb]]*/ !important; 

}


.dropdown,
.welcome-box *,
.slide *,
.indicator,
[class^="hero-nav"],
[class="mod mod-mig mig"] *
{
    background-color: rgba(0, 0, 0, 0) !important;
}



[class*="inactive"] *,
.inactive
{
    color: gray !important;
    background: none transparent! important;
    border-color: /*[[BgColor]]*/ !important; 
}

input:not([class="form-submit"]),
[class="link-menu"] *:not(:hover):not(button):not(svg):not([class*="icon"]),
[class="ftd-box-content"] *,
[class^="pos-icon-item__wrapper"],
[class="control controlSec"]:not(:hover) 
{
  color: white !important;
  /*background-color:inherit !important;*/
}


#id155c,
[class^="fc-icon"],
.cke_button_icon,
.layer-container,
.dropdown {
    filter: hue-rotate(180deg) invert(100%)  brightness(120%) contrast(120%) saturate(150%);
}


/* Scrollbars */
*::-webkit-scrollbar {
    width: 12px;
    scrollbar-face-color: /*[[ScrThumb]]*/;
    scrollbar-shadow-color: #FFFFFF;
    scrollbar-highlight-color: #FFFFFF;
    scrollbar-3dlight-color: #FFFFFF;
    scrollbar-darkshadow-color: #FFFFFF;
    scrollbar-track-color: #FFFFFF;
    scrollbar-arrow-color: #FFFFFF;
}
 
*::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
    -webkit-border-radius: 0px;
    border-radius: 0px;
}
 
*::-webkit-scrollbar-thumb {
    -webkit-border-radius: 0px;
    border-radius: 0px;
    background: /*[[ScrThumb]]*/; 
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5); 
}

/* Firefox */
*{
  scrollbar-width: thin;          /* "auto" or "thin"  */
  scrollbar-color: /*[[ScrThumb]]*/ rgba(0,0,0,0.5);;   /* scroll thumb & track */ 
}
