@-moz-document domain("teamup.com") {
#toolbar-1030 .x-toolbar.ext-cal-toolbar.x-docked.x-toolbar-default.x-docked-top.x-toolbar-docked-top.x-toolbar-default-docked-top.x-box-layout-ct.x-noborder-trl #teamup-calendar-panel-multiweek-wk-0#teamup-calendar-panel-multiweek-wk-0 {
	background-image: -moz-linear-gradient(bottom, rgb(232, 242, 248) 0%, rgb(246, 246, 246) 88%)!important;
	background-image: linear-gradient(bottom, rgb(100%, 100%, 100%) 15%, rgb(246, 246, 246) 88%)!important;
	background-image: -webkit-linear-gradient(bottom, rgb(100%, 100%, 100%) 15%, rgb(246, 246, 246) 88%)!important;
	background-image: -o-linear-gradient(bottom, rgb(100%, 100%, 100%) 15%, rgb(246, 246, 246) 88%)!important;
	background-image: -ms-linear-gradient(bottom, rgb(100%, 100%, 100%) 15%, rgb(246, 246, 246) 88%)!important;
}
#teamup-calendar-panel-multiweek-ev-day-20151130 #teamup-calendar-panel-multiweek-ev-day-20151201 #teamup-calendar-panel-multiweek-ev-day-20151202 #ext-element-76 #teamup-calendar-panel-multiweek-day-20151128 #teamup-calendar-panel-multiweek-day-20151109 .ext-cal-day-today {
	background: rgba(56, 146, 211, 0.69) !important;
}
#teamup-calendar-panel .x-toolbar {
	background-color: #E20000 !important;
}
.x-btn-default-toolbar-small {
	border-color: rgba(0, 0, 0, 0.075) !important;
}
.x-btn-default-toolbar-small {
	border-radius: 3px;
	padding: 3px 3px 3px 3px;
	border-width: 1px;
	border-style: solid;
	background-color: #2a2a2a !important;
}
#teamup-calendar-panel #teamup-calendar-panel-tb-refresh #app-nav-picker #app-nav-picker-eventEl #app-nav-picker-eventEl #app-nav-picker {
	background-color: #222 !important;
	border-color: #1a1a1a !important;
}
.x-btn.x-btn-disabled.x-btn-default-toolbar-small {
	background-image: none;
	background-color: #151515 !important;
}
.teamup-cal-nav-picker {
	background-color: #2a2a2a !important;
}
.teamup-cal-nav-picker {
	color: #000;
	cursor: pointer;
	padding: 0 1px 0 0 !important;
	border: 0;
}
.teamup-cal-nav-picker .x-datepicker-cell {
	border: none;
	vertical-align: top;
}
.x-datepicker-cell {
	text-align: right;
	border-width: 1px !important;
	border-style: solid !important;
	border-color: #2A2A2A !important;
}
.teamup-cal-nav-picker .x-datepicker-date {
	color: #283842 !important;
	border: 1px solid #131313 !important;
}
.teamup-cal-nav-picker .x-datepicker-highlight .x-datepicker-date,
.teamup-cal-nav-picker .x-datepicker-selected .x-datepicker-date {
	background-color: #151515 !important;
	border-color: #1a1a1a !important;
	border-bottom-color: #151515 !important;
}
#tp-calendarlist_header body,
.x-calendar-list .x-panel-header .x-tool-img,
.x-calendar-list .x-toolbar {
	background-color: #1a1a1a !important;
}
#tp-calendarlist-body #app-west .x-panel-body {
	background-color: #222 !important;
}
#tp-calendarlist,
.x-calendar-list .x-panel-header,
#tp-calendarlist .x-panel-body,
.x-calendar-list .x-panel-header .x-tool-img,
.x-calendar-list .x-toolbar {
	background-color: #0F1115;
}
#app-west .x-panel-body {
	background-color: #151719;
}
.x-calendar-list .x-panel-body {
	border-top: none;
	border-bottom: none;
}
.x-panel-body-default {
	background: #CCC0C0;
	border-color: #252121;
	color: #000;
	font-size: 13px;
	font-weight: 300;
	font-family: helvetica, arial, verdana, sans-serif;
	border-width: 1px;
	border-style: solid;
}
.x-calendar-list .ext-cal-res {
	background-color: #171717;
	color: #283842;
}
.teamup-cal-nav-picker .x-datepicker-header {
	background-color: #1a1a1a;
}
#teamup-calendar-panel-multiweek #teamup-calendar-panel-body {
	background-color: #1a1a1a;
}
#teamup-calendar-panel-multiweek #teamup-calendar-panel-multiweek-wk-0 {
	border-color: #484848 !important;
}
#teamup-calendar-panel-body #teamup-calendar-panel-multiweek .ext-cal-day-we {
	background-color: #151515 !important;
	background-image: none !important;
	color: #999999 !important;
}
.ext-cal-day-today {
	background: #9E0000 !important;
}
.ext-cal-monthview .ext-cal-body-ct {
	background: #1B1616 !important;
}
#teamup-calendar-panel-multiweek #teamup-calendar-panel-multiweek-wk-0 {
	border-color: #484848 !important;
	background-image: none !important;
}
}
