css:
@-moz-document domain("moodle.port.ac.uk") {
body, #content, #header, #footer, .que .formulation, .breadcrumb-nav .breadcrumb, .que .info, .que .info .editquestion img, .que .info .questionflag img, .que .info .questionflag input, #page-my-index .block_course_overview, .block .content, footer#page-footer, #bottomLinks, .loginbox.twocolumns .loginpanel, .loginbox .desc, .forumpost.clearfix.lastpost.firstpost.starter, .section.main.section-summary.clearfix.current, .block_login.block, #frontTipsContent, .no-overflow div, .content, #section-11, .no-overflow span, td, .qn-question, .block_settings.block, .view-container-border, .no-overflow pre, .level1.levelodd.oddd1.baggt.b2b.column-itemname, .userprofile, .contactselector.mdl-align, .messagearea.mdl-align, .messagesendbox, .visible{
    background-color: black !important;
    color: gray !important;
}

a{
    color: #669CFF !important;
}

pre{
    color: #BA4F02 !important;
}

.dropdown a{
    color: lightgray !important;
}

h2, .dropdown-menu>li>a{
    color: lightgray !important;
}

input, .que.shortanswer .answer{
    background-color: #A8A8A8 !important;
    color: black !important;
}

#page-my-index .course_title a{
    color: gray !important;
}

.block{
	border: none !important;
}

.navbar-nav>li>.dropdown-menu{
    border: solid 1px lightgray !important;
    background-color: black !important;
}

#region-main .no-overflow p span strong{
    background-color: black !important;
    color: lightblue !important;
}

.discussion.r0{
    background-color: #242424 !important;
}

.discussion.r1{
    background-color: #030303 !important;
}

#page-mod-forum-discuss .discussioncontrols .discussioncontrol .singleselect select.singleselect, select{
    background-color: #454647 !important;
    color: lightgray !important;
}

.owl-page{
    background-color: black !important;
    color: lightblue !important;
}

.frontArticlesContentItem, .frontArticlesTitle, #frontTipsContent{
    border: solid gray 1px;
}

img[src="http://moodle.port.ac.uk/blocks/archivelisting/img/archive.png"]{
    content:url("http://i.imgur.com/GpJmbgV.png");
}

.level1.levelodd.oddd1.b1b.b1t.column-itemname{
    background-color: #59379E !important;
    color: lightgray !important;
}
}
