css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("irccloud.com") {
  body {
    color: #657b83;
  }
  .new {
    color: #dc322f;
  }
  .bufferinputcell, div.buffermain, div.buffermainwrapper, .mainContainer {
    background-color: #fdf6e3;
  }
  div.log {
    color: #93a1a1;
  }
  div.log div.chat b, div.log div.chat strong, div.log div.me b, div.log div.me strong, div.log div.table b, div.log div.table strong, div.log div.groupedLines b {
    font-weight: bold;
  }
  div.log div.chat b, div.log div.chat strong, div.log div.me b, div.log div.me strong, div.log div.groupedLines b, div.log div.status b, div.log div.status strong, div.log div.table, div.log div.table b, div.log div.table strong, div.log div.notice span.message b, div.log div.monospace span.message b {
    color: inherit;
  }
  div.log div.chat, canvas.clock {
    color: #657b83;
  }
  div.log div.self, div.log div.row.highlight {
    color: #586e75;
    background-color: #eee8d5;
  }
  div.log div.me, div.log div.highlight span.date, div.log div.searchMatch span.date {
    color: #586e75;
  }
  div.log div.status, div.log div.groupedLines, div.log div.notice {
    color: #93a1a1;
    background-color: #eee8d5;
  }
  div#timeContainer, div.log span.date {
    color: #93a1a1;
  }
  div.log div.collapsedHead {
    border: none;
  }
  div.log div.collapsedHead.expanded, div.log div.collapsedHead:hover {
    background-color: #eee8d5;
    border: none;
  }
  div.log div.discoGroup.expanded, div.log div.joinPartGroup.expanded {
    border-color: #eee8d5;
  }
  div.log div.backlogDivider hr, div.log div.fetch hr {
    border-color: #eee8d5;
  }
  .extras span.extrasBar, .extras span.extrasBar:hover {
    color: #93a1a1;
    background-color: #eee8d5;
    border: none;
  }
  body.app .loadMore__button a, body.app .loadMore__button span, body.app .loadMore__button:hover:not([disabled]) a, body.app .loadMore__button:hover:not([disabled]) span {
    color: #93a1a1;
    background-color: #eee8d5;
    box-shadow: none;
  }
  div.log div.bottomSeen span, div.log div.focusSeen span, div.log div.lastSeen span.text {
    color: #93a1a1;
    background-color: #eee8d5;
  }
  div.dateWrapper table.date, div.log div.dateChange p {
    color: #93a1a1;
    background-color: #fdf6e3;
    border-bottom: 1px solid #eee8d5;
    box-shadow: none;
  }
  div.log span.collapseWidget .fa {
    color: #657b83;
  }
  div.log div.collapsedHead:hover span.collapseWidget .fa {
    color: #586e75;
  }
  div#sidebarwrapper {
    background-color: #fdf6e3;
    border-color: #eee8d5;
    box-shadow: 5px 0px 0px 0px #eee8d5 inset;
  }
  ul.memberList .bufferLink, ul.memberList li .bufferLink:hover {
    color: #586e75;
  }
  a, body.irccloud .userError a, body.irccloud .userInfo a, body.irccloud .userSuccess a, button.link, span.link, div.log a, div.log a:hover, div.log div.chat a, div.log div.chat a:hover, div.log div.me a, div.log div.me a:hover, div.log div.status a, div.log div.stats a:hover, #footer a, #sidebar #limits a, h1#title a, #footer a:hover, #sidebar #limits a:hover, h1#title a:hover, div.log div.groupedLines a, div.log div.notice a {
    color: inherit;
  }
  .bufferHead {
    color: #586e75;
    background-color: #eee8d5;
    border: none;
  }
  .bufferstatus div.status h2.bufferHeading span.label {
    color: #586e75;
  }
  .bufferstatus div.status p.buttons {
    color: #657b83;
  }
  .bufferstatus .bufferOptions:hover, .totalMemberCount:hover {
    color: #586e75;
  }
  .bufferList .active span.buffer {
    color: #657b83;
  }
  .bufferList .active:hover span.buffer {
    color: #586e75;
  }
  .bufferList .active.unread span.buffer {
    border-color: #268bd2;
  }
  .bufferList .buffer:hover span.buffer {
    background-color: #eee8d5;
  }
  .bufferList .selected span.buffer, .bufferList .selected:hover span.buffer {
    color: #fdf6e3;
    background-color: #657b83;
    border-color: #586e75;
  }
  .bufferList .active.selected span.buffer, .bufferList .active.selected:hover span.buffer {
    background-color: #657b83;
    border-color: #586e75;
  }
  .bufferList h2 span.buffer {
    box-shadow: none;
    background-color: #eee8d5;
    border-color: #eee8d5;
  }
  .bufferList h2:hover span.buffer {
    border-color: #eee8d5;
  }
  .bufferList h2.active.selected span.buffer, .bufferList h2.active.selected:hover span.buffer {
    box-shadow: none;
  }
  .bufferList .bufferOptions {
    color: #93a1a1;
  }
  .bufferList .buffer:hover .bufferOptions {
    color: #586e75;
  }
  ul.memberList li.category h2, ul.memberList li.category h2 span.memberCount {
    color: #657b83;
  }
  span.mode_OP, ul.memberList li.ops h2, ul.memberList li.ops h2 span.memberCount {
    color: #859900;
  }
  span.mode_HALFOP, ul.memberList li.halfops h2, ul.memberList li.halfops h2 span.memberCount {
    color: #2aa198;
  }
  span.mode_VOICED, ul.memberList li.voiced h2, ul.memberList li.voiced h2 span.memberCount {
    color: #b58900;
  }
  div.memberwrapper {
    background-color: #fdf6e3;
    box-shadow: 3px 0px 0px 0px #eee8d5 inset;
    border-color: #eee8d5;
  }
  ul.memberList li.category {
    box-shadow: 3px 0px 0px 0px #eee8d5 inset;
  }
  ul.memberList li.ops {
    background-color: inherit;
    color: #859900;
    border-color: #eee8d5;
    box-shadow: 3px 0px 0px 0px #859900 inset;
  }
  ul.memberList li.halfops {
    background-color: inherit;
    color: #2aa198;
    border-color: #eee8d5;
    box-shadow: 3px 0px 0px 0px #2aa198 inset;
  }
  ul.memberList li.voiced {
    background-color: inherit;
    color: #b58900;
    border-color: #eee8d5;
    box-shadow: 3px 0px 0px 0px #b58900 inset;
  }
  .buffernick .nick, div.log div.row span .bufferLink.author {
    color: #586e75;
  }
  .c0, .c1, .c2 {
    color: #b58900 !important; /* yellow */
  }
  .c3, .c4, .c5, .c6 {
    color: #cb4b16 !important; /* orange */
  }
  .c7, .c8, .c9 {
    color: #dc322f !important; /* red */
  }
  .c10, .c11, .c12 {
    color: #d33682 !important; /* magenta */
  }
  .c13, .c14, .c15, .c16 {
    color: #6c71c4 !important; /* violet */
  }
  .c17, .c18, .c19 {
    color: #268bd2 !important; /* blue */
  }
  .c20, .c21, .c22 {
    color: #2aa198 !important; /* cyan */
  }
  .c23, .c24, .c25, .c26 {
    color: #859900 !important; /* green */
  }
  body.irccloud span.maroon, body.irccloud span.red {
    color: #dc322f !important; /* red */
  }
  body.irccloud span.purple, body.irccloud span.magenta {
    color: #d33682 !important; /* magenta */
  }
  body.irccloud span.yellow {
    color: #b58900 !important; /* yellow */
  }
  body.irccloud span.orange {
    color: #cb4b16 !important; /* orange */
  }
  body.irccloud span.green, body.irccloud span.lime {
    color: #859900 !important; /* green */
  }
  body.irccloud span.cyan, body.irccloud span.teal {
    color: #2aa198 !important; /* cyan */
  }
  body.irccloud span.navy {
    color: #268bd2 !important; /* blue */
  }
  #sidebar .info {
    background-color: #eee8d5;
    color: #586e75;
  }
  #sidebar .info h2, #sidebar #limits a:hover h2 {
    background-color: #657b83;
    color: #fdf6e3;
  }
  #sidebar #limits h2 {
    border: none;
  }
  #sidebar #limits a span.highlight, #sidebar #limits a:hover span.highlight {
    font-weight: bold;
    color: inherit;
  }
  #sidebar #limits span.trialRemain {
    background-color: #586e75;
    color: #eee8d5;
  }
  .accountMenu__button {
    background-color: #eee8d5;
    color: #586e75;
    border: none;
    box-shadow: none;
  }
  .accountMenu__caret {
    color: inherit;
  }
  .accountMenu__button:hover .accountMenu__caret {
    color: #839496;
  }
  .themeChooser__layout {
    background-color: #eee8d5;
  }
  .themeChooser__dawn .themeChooser__label {
    font-size: 0;
  }
  .themeChooser__dawn .themeChooser__label:before {
    font-size: 14px;
    content: "Solarized";
  }
  .sidebarChooser--selected .sidebarChooser__layout, .themeChooser--selected .themeChooser__layout {
    border: 2px solid #839496;
  }
  .sidebarChooser__layout, .themeChooser__layout {
    border: 2px solid #eee8d5;
  }
  .sidebarChooser:hover .sidebarChooser__layout, .themeChooser:hover .themeChooser__layout {
    border-color: #586e75;
  }
  .sidebarChooser--selected .sidebarChooser__label, .sidebarChooser:hover .sidebarChooser__label, .themeChooser--selected .themeChooser__label, .themeChooser:hover .themeChooser__label {
    color: #586e75;
  }
  .sidebarChooser__sidebar {
    background-color: #eee8d5;
    border-color: #eee8d5;
  }
  body.irccloud button a, body.irccloud button a:hover, body.irccloud button span {
    background-color: #eee8d5;
    border: 2px solid #93a1a1;
    box-shadow: 0px 1px 0px #eee8d5;
    color: #586e75;
    text-shadow: none;
  }
  body.irccloud button:focus a, body.irccloud button:focus span {
    box-shadow: 0px 2px 0px #eee8d5;
    border-color: #657b83;
  }
  body.irccloud button.active a, body.irccloud button.active span, body.irccloud button:hover:not([disabled]) a, body.irccloud button:hover:not([disabled]) span {
    background-color: #657b83;
    color: #fdf6e3;
  }
  body.irccloud button.action a, body.irccloud button.action span {
    background-color: #859900;
    color: #002b36;
    box-shadow: 0px 1px 0px #eee8d5;
  }
  body.irccloud button.action:hover:not([disabled]) a, body.irccloud button.action:hover:not([disabled]) span {
    color: #fdf6e3;
    background-color: #859900;
  }
  body.irccloud button.action:focus a, body.irccloud button.action:focus span {
    box-shadow: 0px 2px 0px #eee8d5;
    border-color: #657b83;
  }
}
