css:
@-moz-document domain("redditgifts.com"), domain("redditgifts.zendesk.com") {
body, html, .playground-area, .header-nav-container, .header-user-bar-container, .hero-img-container, .current-exchange__sign-up-wrapper, .gallery-filter.filter-exchange, .js-comment.gift-comment, .elves-benefits, .msg-write__body__editor.js-messages-body, .ema-exchange-prefs, .ema-exchange-full, .ema-exchange-full.ng-binding span, .ng-pristine.ng-valid, .exchange-support__fields, .exchange-support__fields input, .exchange-support__exchange, .exchange-support__message, .msg-write__header, .msg-write__header input, .profile-form__header, .profile-form__about-wrap, .profile-form__fieldset, .profile-form__foot, .profile-form__block-contain input, .profile-form__block-contain textarea, .profile-form__block-contain select, .search input, .signup__account-info, .signup__personal-info, .signup__account-info input, .signup__personal-info input, .signup__personal-info select, .orders__cards-title, .orders__title-wrap, #id_comment, .exchanges-notify-form.js-exchanges-notify-form{
    background-color: black !important;
    color: lightgray !important;
}

a{
    color: #1AB3F3;
}

.footer-body, .profile-box, .login__box{
    background-color: #0B0B0B !important;
}

.exchanges-how-to__steps__item__img[alt="Sign up & get matched"]{
  	content: url(http://i.imgur.com/3ZID2hk.jpg) !important;
}

.exchanges-how-to__steps__item__img[alt="Find the perfect gift & send it"]{
    content: url(http://i.imgur.com/LXdQjUG.jpg) !important;
}

.exchanges-how-to__steps__item__img[alt="Receive a gift from your match"]{
    content: url(http://i.imgur.com/LnGgpVG.jpg) !important;
}

.exchange-group__exchange.ng-scope, .ema-stats__exchange-stat{
    background-color: #474747 !important;
    color: #D3D3D3 !important;
}

.info-flash.ema-flash, .info-flash.flash-major.ema-error-flash.ng-binding{
    background-color: #333333 !important;
    color: #1AB3F3 !important;
}

.ema-select-title.ng-binding{
    color: #D11C00 !important;
}

.user-menu-item a, .my-account.js-trigger-user-menu, .gallery-item__info{
    background-color: black !important;
}

.gallery-item, .msg-write__body__editor.js-messages-body, .ng-pristine.ng-valid, .exchanges-notify-form__email.js-exchanges-notify-email{
    border: solid #313131 2px !important;
}

#exchange_credits tr:nth-child(odd), .messages-nav, .msg-short.msg-short--inbox, .msg-short.msg-short--sent, .msg-short.msg-short--ignored, .ng-pristine.ng-valid, .exchanges-notify-form__email.js-exchanges-notify-email{
    background-color: #0C0C0C !important;
}

.msg-short.msg-short--inbox, .msg-short.msg-short--sent, .msg-short.msg-short--ignored{
    border: solid 1px;
    color: lightgray !important;
}

.msg-short__element{
    color: lightgray !important;
}

.msg-long__header{
    background-color: #337AA3 !important;
}

#exchange_credits tr:nth-child(even), .address-container, .profile-form__field-heading{
    background-color: #161616 !important;
}

.my-account.js-trigger-user-menu{
	border-right: solid black 12px;
    border-left: solid black 10px;
    border-top: solid black 6px;
    border-bottom: solid black 10px;
}

.gallery-types__item__select-item.btn-text{
	background-color: #CF3703 !important;
    color: #9ADEF3 !important;
}

.gallery-filter.filter-exchange{
    border: solid #D3D3D3 1px !important;
}

.elves-subscriptions__choice__content{
    background-color: #3A3A3A !important;
}

.elves-subscriptions__choice__content__desc, .elves-subscriptions__choice__content__desc-more__p, .elves-subscriptions__heading{
    color: #D3D3D3 !important;
}

.ng-valid.ng-dirty{
    background-color: #0C0C0C !important;
    color: lightgray !important;
    border: solid #313131 2px !important;
}

.ng-valid.ng-dirty:focus, .ng-pristine.ng-valid:focus, .ema-col-block-inner, .ema-field-upload-p, .footer-head{
    color: black !important;
}

.mark-shipped-input.ng-valid.ng-dirty, .mark-shipped-input.ng-pristine.ng-valid, .ema-field-input.ng-valid.ng-dirty, .ema-field-input.ng-pristine.ng-valid, .ema-field-textarea.ng-valid.ng-dirty, .ema-field-textarea.ng-pristine.ng-valid, .ema-field-select.ng-valid.ng-dirty, .exchanges-notify-form__email.js-exchanges-notify-email{
    color: #AAAAAA !important;
}

.article-header.clearfix h1, h3, h4{
    color: #72D0F7 !important;
}

.exchange-support__heading{
    color: white !important;
}

.profile-form__about-wrap{
    border: solid #161616 2px !important;
}

.search input, .signup__account-info input, .signup__personal-info input{
    border: solid white 1px !important;
}

.exchanges-section__heading{
    background-color: #106D93 !important;
}

.tile-icon.disabled{
    background-color: #999999 !important;
}
}
