css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
	
@-moz-document url("chrome://browser/content/browser.xul")
{
	window
	{
		padding-top: 29px !important;
	}


	
	 .tabbrowser-tabs,.tabbrowser-tabs tab, .tabbrowser-strip, .tabs-bottom, #stumbleupon
	{  
		background: none !important;
		border: 0 !important;
		margin: 0 !important;
		-moz-border-bottom-colors: none !important;
		-moz-border-left-colors: none !important;
		-moz-border-radius: 0 !important;
		-moz-border-right-colors: none !important;
		-moz-border-top-colors: none !important;
                padding: 0 !important;
		
	}
	
	.tabbrowser-strip
	{
		background-color: black !important;
		height: 29px !important;
		position: fixed !important;
		top: 0 !important;
		width: 100% !important;
	}
	.tabbrowser-tabs
	{   
		-moz-appearance: none !important;
		
		width: 100% !important;
	}
	.tabbrowser-tabs tab, .tabbrowser-tabs tab[selected="true"], .tabbrowser-tabs, #stumbleupon
	{
		color: white !important;
	}
	.tabbrowser-tabs tab
	{
		
		padding:0 !important;
		margin:0 !important;
		height:26px !important;
	}
	

tab[selected="true"]

	{color: white !important;
	background-color: -moz-rgba(0,255,255,.25) !important;
border-bottom: 1px solid -moz-rgba(0,255,255,0.99) !important;
		border-right: 1px solid -moz-rgba(0,255,255,0.99) !important;}

tab[selected="true"]:hover
{background-color: -moz-rgba(0,255,255,.5) !important;}


.tabbrowser-tabs tab:not([selected="true"]):hover
               {background-color: -moz-rgba(0,100,155,.5) !important;
		border-bottom: 1px solid -moz-rgba(0,100,155,0.99) !important;
		border-right: 1px solid -moz-rgba(0,100,155,0.99) !important;}
.tabbrowser-tabs tab:not([selected="true"]) {
border-bottom: 1px solid -moz-rgba(255,255,255,0.85) !important;
border-right: 1px solid -moz-rgba(255,255,255,0.85) !important; }



	
	
	tab[selected="true"] label
	{
		color: white !important;
		font-weight: bolder !important;
	}
	
	.tabbrowser-tabs tab
	{
		border-left: 1px solid -moz-rgba(255,255,255,0.85) !important;
		border-top: 1px solid -moz-rgba(255,255,255,0.85) !important;		
		margin-right:1px !important;
		-moz-border-radius-topleft:5px !important;
		-moz-border-radius-topright:5px !important;
-moz-border-radius-bottomleft:5px !important;
-moz-border-radius-bottomright:5px !important;
                
	}
	

}
