css:
@-moz-document domain("sh.st") {
#footer, .advert, .skip-advert, .skip-logo {
 display: none !important;   
}

#skip-top-bar {
    height: 100%;
    z-index: 1000;
}

#timer {
    display: block;
    top:10%;
    left:50%;
    width: 200px;
    margin-left: -100px;
    color: black;
    font-weight: 400;
}

#skip_button {
    top:10%;
    left:50%;
    width: 200px;
    margin-left: -100px;
    float: none;
    position: absolute;
    z-index:100;
    background: #57f;
    color: white;
}

.skip-add .skip-top-bar .skip-add-container .skip-btn:after{
    border-left-color: white;
}

.skip-add .skip-top-bar .skip-add-container {
    background: white;
    z-index: 1000;
    position: fixed;
    height: auto;
    top:0;left:0;right:0;bottom:0;
}
}
