css:
@-moz-document domain("youtube.com") {
* {
    transition: background-color 200ms ease, color 200ms ease !important;
}
body,
ytd-app,
ytd-watch,
ytd-watch-flexy,
.ytd-watch-flexy,
ytd-browse[page-subtype="channels"] {
    background: #000 !important;
}
#youtube-paths.yt-icon path,
#guide-icon .yt-icon path,
.yt-dropdown-menu g.style-scope.yt-icon path:not([fill="none"]),
#youtube-paths path {
    fill: #ccc !important;
}
.guide-pinned.show-guide .guide-pinning-enabled #page,
.guide-pinned.show-guide .guide-pinning-enabled #appbar-content {
    transition: padding-left 200ms ease !important;
}
app-drawer {
    transition: right 200ms ease !important;
}
#yt-masthead-container,
ytd-masthead {
    background: #000 !important;
    border: 0 !important;
    overflow: visible !important;
}
#yt-masthead-container::after,
ytd-masthead::after {
    content: "" !important;
    position: absolute !important;
    bottom: 0 !important;
    left: 0 !important;
    display: block !important;
    width: 100% !important;
    height: 2px !important;
    background: #fff !important;
    visibility: visible !important;
    z-index: 10 !important;
}
.yt-card {
    background: #0f0f0f !important;
}
#masthead-appbar,
#guide-content.ytd-app {
    background: #0f0f0f !important;
    border-bottom-color: #111 !important;
    z-index: 8 !important;
}
#appbar-nav .epic-nav-item-heading,
#metadata-line.ytd-grid-video-renderer {
    color: #ccc !important;
}
#guide #appbar-guide-menu {
    background: #000 !important;
    border-right-color: #111 !important;
}
.yt-ui-ellipsis {
    background: transparent !important;
}
body #footer-container {
    background: #000 !important;
    border-top-color: #1f1f1f !important;
}
#footer-main {
    border-bottom-color: #0f0f0f;
}
.branded-page-module-title a:visited,
.branded-page-module-title a {
    color: #ccc !important;
}
#browse-items-primary .item-section>li>.yt-lockup-tile,
.branded-page-v2-primary-col .branded-page-box,
.section-list li .item-section .branded-page-box {
    border-bottom-color: #1f1f1f !important;
}
#appbar-guide-menu {
    transition: all 200ms ease !important;
}
.guide-section-separator {
    border-color: #1f1f1f !important;
}
.guide-item:hover {
    color: #fff !important;
}
.guide-item.guide-item-selected,
.guide-item.guide-item-selected:hover,
.guide-item.guide-item-selected .yt-deemphasized-text,
.guide-item.guide-item-selected:hover .yt-deemphasized-text {
    color: #fff !important;
}
#masthead-search-terms {
    background: #000 !important;
    box-shadow: none !important;
}
.compact-shelf .yt-uix-button-shelf-slider-pager {
    background: #fff !important;
    transition: all 200ms !important;
}
.yt-default h1,
.yt-default h2,
.yt-default h3,
.yt-default h4,
.yt-default h5,
.yt-default h6,
h1.yt,
h2.yt,
h3.yt,
h4.yt,
h5.yt,
h6.yt,
#watch-description {
    color: #ccc !important;
}
.comments .comment-text .comment-text-content,
#title.ytd-shelf-renderer,
#metadata-line.ytd-video-meta-block,
.title.ytd-video-primary-info-renderer,
span.yt-view-count-renderer,
.content.ytd-video-secondary-info-renderer,
#content-text.ytd-comment-renderer,
yt-formatted-string.ytd-comments-header-renderer,
#icon-label.yt-dropdown-menu,
h3.ytd-compact-video-renderer,
#byline.ytd-video-meta-block,
.more-button.ytd-video-secondary-info-renderer,
.less-button.ytd-video-secondary-info-renderer,
#toggle.ytd-grid-renderer,
ytd-search-filter-renderer yt-formatted-string.ytd-search-filter-renderer,
#filter-group-name.ytd-search-filter-group-renderer {
    color: #efefef !important;
}
ytd-search-filter-renderer.selected yt-formatted-string.ytd-search-filter-renderer {
    color: #ffffff !important;
    font-weight: 600 !important;
}
#masthead-positioner-height-offset {
    margin-bottom: 16px !important;
}
#masthead-search-terms input {
    color: #fff !important;
}
#watch8-action-buttons {
    border-color: #333 !important;
}
.yt-card .yt-uix-button-expander {
    border-top-color: #333 !important;
}
#watch-header .yt-user-info a {
    color: #fff !important;
}
.yt-uix-button-content .subscribed-label,
.yt-uix-button-content .unsubscribe-label {
    color: #333 !important;
}
.all-comments,
.all-comments a {
    background: transparent !important;
}
.ytp-progress-bar .ytp-play-progress {
    background: #fff !important;
}
.ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
    background: #fff !important;
}
.ytp-volume-slider-track {
    background: #fff !important;
}
.ytp-volume-control-hover .ytp-volume-panel {
    overflow: visible !important;
}

.video-extras-sparkbars {
    overflow: visible !important;
}
.video-extras-sparkbar-likes {
    background: #fff !important;
}
.comment-renderer-text-content {
    color: #ccc !important;
}
.comment-renderer.channel-owner .comment-author-text {
    background: transparent !important;
}
.yt-card .yt-uix-tabs,
#contents.ytd-section-list-renderer > *.ytd-section-list-renderer:not(:last-child),
#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child),
ytd-video-primary-info-renderer,
ytd-video-secondary-info-renderer,
#content.ytd-playlist-video-renderer,
#items.ytd-playlist-sidebar-renderer > *.ytd-playlist-sidebar-renderer:not(:last-child),
#placeholder-area.ytd-comment-simplebox-renderer,
ytd-compact-autoplay-renderer {
    border-bottom-color: rgba(255, 255, 255, 0.25) !important;
}
.yt-card .yt-uix-tabs .yt-uix-button:hover, .yt-card .yt-uix-tabs .yt-uix-button:active, .yt-card .yt-uix-tabs .yt-uix-button.yt-uix-button-active, .yt-card .yt-uix-tabs .yt-uix-button.yt-uix-button-toggled {
    border-bottom-color: rgba(255, 255, 255, 0.6);
}
.video-list .video-list-item .title {
    color: #ccc !important;
}
.watch-title-container .watch-title {
    color: #efefef !important;
}
.input-content.paper-input-container  input, .input-content.paper-input-container  textarea, .input-content.paper-input-container  iron-autogrow-textarea, .input-content.paper-input-container  .paper-input-input,
#edit-buttons ytd-button-renderer #button.ytd-button-renderer {
	color: #fff !important;
}
ytd-browse[page-subtype="playlist"] ytd-two-column-browse-results-renderer.ytd-browse {
	background-color: transparent !important;
}
 ytd-playlist-sidebar-renderer {
    background-color: rgba(255, 255, 255, 0.15) !important;
 }
  h3.ytd-playlist-video-renderer,
  #stats.ytd-playlist-sidebar-primary-info-renderer {
    color: #efefef !important;
}
#like-bar.ytd-sentiment-bar-renderer {
    background: #ffffff !important;
}
  #simplebox-placeholder.ytd-comment-simplebox-renderer {
    color: #efefef !important;
  }
  ytd-toggle-button-renderer.style-default-active[is-icon-button] {
    background-color: rgba(255, 255, 255, 0.16) !important;
    box-shadow: 0 0 12px rgba(255, 255, 255, 0.4) !important;
}
  .date.ytd-video-secondary-info-renderer {
    color: #efefef !important;
  }
#message.ytd-message-renderer {
    color: rgba(255, 255, 255, 0.4) !important;
}
#tabs-inner-container.ytd-c4-tabbed-header-renderer,
#channel-header.ytd-c4-tabbed-header-renderer {
    background: #1f1f1f !important;
}
.dropdown-trigger yt-icon {
    fill: rgba(255, 255, 255, 0.4) !important;
    color: rgba(255, 255, 255, 0.4) !important;
}
.more-button paper-button,
.ytd-pla-shelf-renderer,
.ytd-pla-shelf-item-renderer,
ytd-comment-action-buttons-renderer,
.more-button.ytd-comment-renderer,
.less-button.ytd-comment-renderer {
    color: #ccc !important;
}

#content.ytd-playlist-video-renderer:last-of-type {
    border-bottom-color: transparent !important;
}

ytd-rich-grid-media {
    opacity: 0.7 !important;
    transition: all .2s ease-in-out !important;
}

ytd-rich-grid-media:hover {
    opacity: 1 !important;
}

/*[[element-colors]]*/
}

