css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https:\/\/books\.google\.co\.[a-z]+\/books\/reader.+"),
url-prefix("https://play.google.com/books/reader"),
url-prefix("https://books.googleusercontent.com/books/reader"){
  /* Background */
  body,
  .OjrvKe-Df1ZY-haAclf {
    background-color: #514f4e !important;
  }
  /* Page shadow */
  .OjrvKe-DARUcf-sM5MNb-jyrRxf{
    box-shadow: 2px 10px 20px 5px rgba(10,10,10,0.3);
  }
  /* Page mergin */
  .OjrvKe-DARUcf-sM5MNb-r08add .OjrvKe-DARUcf-sM5MNb-jyrRxf{
    margin-left: 0 !important;
  }
  .OjrvKe-DARUcf-sM5MNb-yXOQKd .OjrvKe-DARUcf-sM5MNb-jyrRxf{
    margin-right: 0 !important;
  }
  /* Header */
  .OjrvKe-ctOWCc {
    opacity: 0 !important;
  }
  .OjrvKe-ctOWCc:hover {
    opacity: 0.75 !important;
  }
  .OjrvKe-Df1ZY-haAclf-Df1ZY {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
  }
  /* Navigation bar */
  .OjrvKe-q8lkkd {
    opacity: 0 !important;
  }
  .OjrvKe-q8lkkd:hover {
    opacity: 0.75 !important;
  }
}
