css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nosub.tv") {
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;
    text-shadow: 0px !important;
    text-shadow: none !important;
  }
  body{
   background:#111;
   color: #ddd;
  }
  .wrapper, .cat_sub ul .odd{
   background:#111;
   border-width: 0px;
  }
  .ad_player, .margin_top,.footer,.notice,.line,#lianyue_single_header_ad,.mood_comment,.breadcrumb,.header .top_logo,.content_single,.right,#lianyue_share,.open_bs, 
  .barrage{
    display:none;
  }
  .header .menu ul {
    padding: 5px 0px 0px;
    text-transform: uppercase;
  }
  .header {
    border-top: 0px;
  }
  .cat_list ul .odd,.cat_list ul li,.wpfp_widget_view ul li,.wpfp_widget_view ul .odd,.index_cat .ul .odd,.index_cat .ul{
    background:#222 !important;
  }
    .cat_list ul .odd:hover,.cat_list ul li:hover,.wpfp_widget_view ul li:hover,.wpfp_widget_view ul .odd:hover,.index_cat .ul .odd:hover,.index_cat .ul:hover{
    background:#222;
  }
  .search_form .field input,.p .single_tag ul li a, .cat_sub ul .odd:hover, .cat_sub ul #right:hover, .cat_sub ul li:hover{
    background:#333;
    color:#eee;
  }
  #lianyue_single_header_ad,#imobile_adspotdiv1,#imobile_adspotdiv2,#imobile_adspotdiv3,#lianyue_top9_header_ad,body > div.wrapper > div.content > div > div.sidebar.slide_js > div.widget_text > div > div,#lianyue_cat_footer_ad {
    display:none !important;
  }
  body > div.wrapper > div.content > div.margin_bottom.p > div.mukioplayer > div.ad_player {
    display:none !important;
  }
  #g_floating_area1,#g_floating_area2{
    display:none !important;
  }
}
