css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.scottaaronson.com/blog/") {
  body, #header, #headerimg, #page, #footer, #content {
    width: auto;
    height: auto;
    background-image: none;
  }
  
  #header {
    margin: 0;
  }
  
  #footer {
    margin: 2em 0 0 0;
  }
  
  #headerimg > .description {
    display: inline-block;
    text-align: left;
    font: bold small-caps 11pt 'Futura LT', 'Universalis ADF Std', 'League Spartan', 'ADAM.CG PRO', Verdana, Roboto, 'Droid Sans', sans-serif;
    color: #9c6;
  }

  body, .commentlist li {
    font-family: 'Droid Sans', 'PT Sans', sans-serif;
  }

  #headerimg > .description > font {
    font: bold 11pt 'PT Sans', Cabin, Roboto, 'Droid Sans', sans-serif;    
  }
  
  #header {
    background: black;
    font-weight: bold;
  }
  
  h1 {
    text-align: left;
    padding: 0;
    margin-right: 1em;
    float: left;
    font: 48pt 'Futura LT', 'Universalis ADF Std', 'League Spartan', 'ADAM.CG PRO', Verdana, Roboto, 'Droid Sans', sans-serif;
    text-transform: uppercase;
  }
  
  h2, h3, h4, h5, h6, #sidebar h2,
  .postmetadata, .navigation, label {
    font-family: 'Futura LT', 'Universalis ADF Std', 'League Spartan', Verdana, Roboto, 'Droid Sans', sans-serif;
    font-weight: normal;
    font-variant: small-caps;
  }
  
  #header {
    padding: 2em;
  }
  
  #page {
    background: white;
    margin: 0;
  }
  
  #content {
    margin-right: 240px;
  }
  
  #sidebar {
    float: right;
    margin-left: -240px;
    margin-right: 20px;
  }
  
  .commentlist > li {
    border-top: none;
    border-bottom: none;    
  }
  
  .commentlist > li {
    border-left: 8px solid #eee;
    padding-left: 36pt;
    margin-left: -36pt;    
  }
  
  .commentlist > li.cmt-the-blogger {
    border-left-color: black;
    background: #f0f0f0;
  }

  .commentlist > li:target {
    border-left-color: blue;
    background: #f0f0ff;
  }
}
