css:
@-moz-document url-prefix("http://eu.battle.net/heroes/ru/forum"), url-prefix("http://us.battle.net/heroes/en/forum/") {
.forums-home .station-content-wrapper {
    position: relative;
    background-color: #454545;
    
    background-repeat: no-repeat;
    border: 1px solid #3B1F4E;
    padding: 16px;
    padding: 1rem;
    z-index: 1;
}
.author-name.blizzard-post {
    color: #11ddff;
}
.forums-home .child-forum {
    max-width: 20rem;
    margin-bottom: 1rem;
}
.forums-home .child-forum {
    border: 1px solid #201436;
    font-size: 16px;
    font-size: 1rem;
    border-image: linear-gradient(#201436,#12112E) 1;
    background-image: linear-gradient(#333,#555);
}

.post-interior { background-color:#444 !important; margin: -15px; min-height: 255px;}
.post-content {color: #999;}
.view-topic .blizzard .post-detail {color: #11ddff;}
.post-content {
    color: #ddd;
    border-top: 2px solid grey;
    margin-top: 10px;
}

.view-topic .inner-container {
    background: #555;
    border-image: linear-gradient(rgba(0,0,0,.5),rgba(0,0,0,.5)) 1;
}

.ui-breadcrumb li .breadcrumb-arrow:after {
    border: 0;
    content: " ";
    display: inline-block;
    background: none;
    width: 12px;
    height: 10px;
    margin: 2px 7px 0 0;
}
nav[role=navigation] {
    background-position: center -.7rem;
}
.view-topic .topic-status {
    display: block;
    clear: both;
    color: #00bb76;
}


.user-details .poster-name {
    color: #5cf;}

.view-topic .mvp .post-detail {
    color: #00cc47;
}
.post-detail blockquote.quote-mvp {
    border-color: #00cc47;
    color: #00cc47;
}
html {
    background-color: #fff;
    color: #fff;
}


.post-detail blockquote.quote-public {
    color: #bbb;
}
.page-container {
    background-color: #333;
}

.forum-topics .topic-title {
    color: #ccc;
    font-size: 14px;
    font-size: .875rem;
    
}


a {
    color: #5fa4c7;
    text-decoration: none;
}

.author-name {
    color: #fff;
    font-size: 13px;
    font-size: .8125rem;
}

.blizz-tracker .inner-container, .search-win .inner-container, .view-forum .inner-container {
    /* position: relative; */
    background-color: #fff;
    background-color: #444;
    background-repeat: no-repeat;
    border: 15px solid #666666;
    z-index: 1;
    padding-right: 10px;
    padding-left: 10px;
    
    
}

.view-forum .forum-topics .reply-cell, .view-forum .forum-topics .view-cell {
    color: #dddddd;
    font-size: 13px;
    font-size: .8125rem;
}


.blizz-tracker .container:before, .search-win .container:before, .view-forum .container:before {
    content: "";
    position: absolute;
    left: 0;
    background: none;
    top: -326px;
    width: 1024px;
    height: 650px;
    max-width: 100%;
    overflow: hidden;
}

.blizz-tracker .container:after, .search-win .container:after, .view-forum .container:after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    background: none;
    bottom: -77px;
    width: 1024px;
    height: 100px;
    overflow: hidden;
    max-width: 100%;
}






.nav-client .footer-content .dropdown-toggle.nav-item, .nav-client .footer-content .nav-client .dropdown-toggle.nav-item, .nav-client .footer-content .nav-item-btn {
    color: #555;
}
.nav-client .nav-a.nav-item:hover, .nav-client a.nav-item {
    color: #eee!important;
    text-decoration: none;
}
.inner-search-form input, .search-bar input {
    color: #bbb;
    border-radius: 0;
    background-color: #000;
    border: 1px solid #fff;
    font-size: 14px;
    font-size: .875rem;
    padding: 12px;
    padding: .75rem;
    margin-top: 16px;
    margin-top: 1rem;
}

.ui-breadcrumb li .breadcrumb-text {
    color: #fff;
    font-size: 14px;
    font-size: .875rem;
    border-bottom: 1px dotted #fff;
    padding-bottom: 0px;
    vertical-align: middle;
    
}
.inner-search-form .inner-search-submit, .search-bar .search-button {
    height: 80%;
}
}
