css:
@-moz-document url("https://test.iptv.bulsat.com/iptv-login.php") {
/*

Title:
    Bulsatcom IPTV 2.4
Tested in:
    Chrome
Changelog v2.4:
     - Fixed the player not being full width and full height and added a logout button to the top right.
Last Updated:
    17.11.2016
Applies to:
    https://test.iptv.bulsat.com/iptv-login.php

*/

body {
    overflow: hidden !important;
}

#jw, .video-js {
    width: 100vw !important;
/*     height: 535px !important; */
    
    height: 100vh !important;
}
.login-links #auth {
    position: absolute;
    top: 1px;
    right: 1px;
    z-index: 500;
    background: url('https://www.feed-forward.net/logout-icon.png') center no-repeat, #d64937;
    background-size: 13px 15px;
    width: 23px;
    height: 25px;
    padding: 10px;
    margin: 0;
    font-size: 0;
}

#epg {
    position: fixed;
    width: 600px;
    background: rgba(0, 0, 0, 0.9);
    /*     bottom: 30px; */
    
    left: initial;
    right: 0;
    top: 25px;
    z-index: 200;
    -webkit-transition: transform 0.1s ease-in-out, box-shadow 0.1s ease-in-out;
    -moz-transition: transform 0.1s ease-in-out, box-shadow 0.1s ease-in-out;
    -o-transition: transform 0.1s ease-in-out, box-shadow 0.1s ease-in-out;
    transition: transform 0.1s ease-in-out, box-shadow 0.1s ease-in-out;
    transform: translate(590px);
    -webkit-transition-delay: 2s;
    /*border: 1px solid #333;*/
    border-left: 3px solid #FD9D28;
    box-shadow: 0 0px 5px 5px rgba(0, 0, 0, 0.75);
}

#epg:hover {
    transform: translate(0px);
    /*box-shadow: 0 0 0 100vw rgba(0, 0, 0, 0.5);*/
    
    -webkit-transition-delay: 0s;
    /* Chrome, Safari, Opera */
    
    transition-delay: 0s;
}

#ascrail2000 {
    z-index: 201;
}

#playlist {
    position: fixed;
    width: 289px !important;
    height: calc(95vh - 25px) !important;
    top: 25px;
    left: 0;
    /*     right: 0; */
    
    background: rgba(0, 0, 0, 0.9);
    z-index: 200;
    -webkit-transition: transform 0.1s ease-in-out, box-shadow 0.1s ease-in-out;
    -moz-transition: transform 0.1s ease-in-out, box-shadow 0.1s ease-in-out;
    -o-transition: transform 0.1s ease-in-out, box-shadow 0.1s ease-in-out;
    transition: transform 0.1s ease-in-out, box-shadow 0.1s ease-in-out;
    transform: translate(-279px);
    -webkit-transition-delay: 2s;
    /*border: 1px solid #333;*/
    border-right: 3px solid #FD9D28;
    box-shadow: 0 0px 5px 5px rgba(0, 0, 0, 0.75);
}

#playlist:hover {
    transform: translate(0px);
    /*box-shadow: 0 0 0 100vw rgba(0, 0, 0, 0.5);*/
    
    -webkit-transition-delay: 0s;
    /* Chrome, Safari, Opera */
    
    transition-delay: 0s;
}

#grp {
    position: fixed !important;
    top: 0;
    left: 0;
    z-index: 201;
    background: rgba(0, 0, 0, 0.75);
}
/* #auth {
    position: fixed;
    top: 20px;
    right: 0;
    z-index: 199;
    opacity: 0.5;
} */

.login-links {
    margin-top: 0;
}

#grp div span {
    /*background: rgba(0,0,0,0.75);*/
}
}
