css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https?://www\\.franina\\.eu/?(cs|fr)?/?.*') {

	/* sections in menus */
	#obalka #ctverec_v_obalce,
	#menu #menu-box.mb
	#ctverce,
  #menu-box {
		background-color: antiquewhite;
	}

	#obalka #ctverec_v_obalce ul li a,
	#obalka #ctverec_v_obalce #mbb,
	#obalka #ctverec_v_obalce #cbb,
	#ctverce ul li a,
	#ctverce #mbb,
	#ctverce #cbb,
	#menu-box #mbb,
	#menu-box #cbb,
	#menu-box ul li a {
		color: black;
	}

  
  
  #mb h3,
  #mb h4 {
    color: black;
  }
  
    
  
  #mb b a,
  #mb a b {
    font-weight: initial;
  }
  
  
	
	#obsah a {
		color: blue;
	}
	

	#menu #menu-box.mb ul li,
	#obalka #ctverec_v_obalce ul li {
		color: black;
		font-weight: bolder;
	}

	#menu #menu-box.mb ul li a,
	#obalka #ctverec_v_obalce ul li a {
		color: black;
		font-weight: normal;
	}

	#menu #menu-box.mb ul li a:hover {
		color: darkgray;
	}



	#lista span a,
	#lista_fr span a {
		background-color: transparent;
		font-weight: bold;
		font-size: 200%;
		color: black;
	}

  
  
	a:hover {
		color: darkgray !important;;
	}
 
	#logo {
		margin-bottom: -100px;
		margin-top: -100px;
		z-index: -10000;
	}
  
  #prouzek {
    visibility: hidden;
  }
  
  #paticka {
		background-color: transparent;
		color: darkgray;
	}

}
