css:
@-moz-document url-prefix("https://webmail.freenet.de/") {
#mainBackgroundDiv {
    background-color: #575757 !important;
}
#cockpit {
    background-color: #575757 !important;
}
.storage {
    color: #E0E0E0;
}
div#salutation {
    color: #E0E0E0;
}
#frnheadUserInfoAndFlyout div {
    color: #E0E0E0;
}
#sectionBars .ui-state-default .ui-button-text {
    color: #E0E0E0;
}
.freenetLogo.isMailProduct {
    background-image: url(http://www.boerse.sx/static/images/boerse.png);
    display: none !important;
}
div#headSearch { 
    display: none !important;
}
.appLink, .appLinkTablet {
	background-color: #2C2C2C;
    -webkit-filter: brightness(2);
    border-style: groove;
    border-color: #787878;
}

#cockpitFooterTable {
    display: none;
}

.cloudSeal {
display:none;
}

#freenetPortalSet {
	    background-color: #575757;
}

frame[name="dotboy"] {
     background-color: #575757;   
}

#naviSubEntries {
background-color: #575757;
}

#EmailSSL{
display:none;
}

.naviBg {
	background-color: #575757 !important;
    border: none;
    
}

.skinBg, 
.skinBg *,
.searchBox *,
.toolBarMain *,
#folderTreeTd,
#contentWrapperTd,
#bot_Email,
#top_Email,
thead th *,
.toolTippBackground{
	background-color: #575757 !important;
    color: #E0E0E0 !important;
}


#Email_id_Email_Tb_actions,
#Email_id_Email_Tb_views{
	background-image: none !important;
}

#botBody_Email[src="/empty.html"],
#btnContainer_EmailTb > td > img[src$="/trenner.png"],
.helpShackle{
    display: none !important;
}

#mainTable,
#target,
#iframeemail {
	width: 100% !important;
}

thead th {
	border: 1px solid #E0E0E0 !important;
    border-bottom: 0 !important;
    border-left: 0 !important;
}

.tafelTree_root a {
	color: #fff;
}

.skinGradiantActive-26 {
    background-color: #393939;
}

div#divMainWrapper,
div#navBarTop {
        background-color: #0F0F0F;
}

#rightAdTag,
#rectangleContainer,
#containerDrop{
	display: none !important;
}

div#mainHead {
	border-bottom-Color: #0F0F0F;
}

.blockUI blockMsg blockPage{
    background-color: #0F0F0F;
}

#tableScrollContainer_Email{
    background-color: #575757 !important;
}

#folderTreesDIV img {
    -webkit-filter: grayscale(1);
}

#folderTreesDIV * {
    color: #E0E0E0 !important;
}

#main {
    position: absolute;
    top: 10px; right: 10px; bottom: 10px; left: 10px;
    /*border: 1px solid red;*/
}

#navBarTop {
    height: 30px;
    /*background-color: rgba(255, 0, 0, 0.2);*/
}

#target {
    position: absolute;
    top: 30px; right: 0; bottom: 0; left: 0;
    /*border: 1px solid green;*/
    height: initial !important;
}

#target > :first-child,
#iframeemail {
    height: 100% !important;
}

.itemsPerFolder {
    display: initial !important;
}


#sectionBars .button {
    background-image: none !important;
    padding: 0;
    margin-right: 15px;
}

#sectionBars .sectionBarSeparator {
    display: none !important;
}

#loading img {
    display: none;
}

#loading td:first-child {
    width: 20px;
    height: 20px;
    background-size: 20px 20px;
    background-image: url( "http://www.lettersmarket.com/uploads/lettersmarket/blog/loaders/common_metal/ajax_loader_metal_512.gif");
}
}
