css:
@-moz-document domain("www.teamliquid.net") {

.titelbalk .tooltip span.tt-userinfo {
	vertical-align: middle;
	color: #3f5370;
}
.titelbalk .tt-userinfo {
	color: #3f5370;
}
.titelhigh .tt-userinfo {
	color: #c4daec;
}
.titelhigh .submessage,.titelhigh .fpost-username {
	color:#fff!important;
}
.submessage {
    color: #373837 !important;
}
.lev-none {
	color: #373837;
}
.userarrow {
	display: inline-block;
	margin-left: 4px;
	background: transparent url("http://www.teamliquid.net/staff/wo1fwood/TL/topbannersprite.png") no-repeat scroll -43px -50px;
	vertical-align: middle;
	height: 10px;
	width: 10px;
}
.forumPost {
	color: #000;
}
.forumsig {
	color: #555;
}
.quote {
	color: #555;
}
.quote:hover {
	color: #000;
}
.sidebar_block .block-news_left_mid a.comments {
	color: #555 !important;
}
#main-left-sidebar {
	color: #000 !important;
}
.forumPost a:link {
	color:#669acc;
}
.forumPost a:visited {
	color:#336799;
}

}
