css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("biblegateway.com") {

/* Text settings */
  /* Sizes and weights */
H3{
  font-size: 28pt !important;
}

.text,
.chapternum{
  font-size: 22pt !Important;
}

H3,
.versenum{
  font-weight: bold !Important;
}

.versenum{
  font-size: 12pt !important;
  line-height: 17px !important; /* Vertical alignment */
}
  /* Colours */
.passage-tools2 *:not(.translation-dropdown):hover,
.text,
.chapternum,
.passage-wrap H4,
.passagenote-text,
.publisher-info-bottom *{
  color: #ffffff !important;
}

.woj{
  color: #ee0000 !important;
}

.passage-wrap A,
.passagenote-bcv{
  color: #ffffa5 !Important;
}

.publisher-info-bottom A{
  color: #0000ee !Important;
}

.passage-tools2 *,
.passage-options-list LI SPAN{
  color: #c7c3c1 !Important;
}

/* BG colours */
body{
  background-color: #000000 !important;
}

.passage-tools2,
.passage-options-list LI,
.qtip-content .content{
  background-color: #231f20 !important;
}

.qtip-titlebar,
.qtip-bg-default{
  background-color: #302e2f !important;
  background-image: none !Important;
}

.passage-col,
.passage-text,
.passage-bible,
.passage-tools2 SPAN:hover,
.passage-tools2 A:hover,
.passage-tools2 .icon-close,
.passage-options-list{
  background-color: transparent !important;
}
/* Object positioning */
.narrow-resources,
.passage-content{
  margin-top: 0px !Important;
}

.passage-bible{
  top: 0px !Important;
  left: 0px !important;
}

.chapternum{
  top: 0px !Important;
}
/* Disappear */
#qtip-10-title{
  background-image: none !Important;
}
.passage-tools2{
  border: none !Important;
}
HEADER,
.bg-search-logo,
.search-section,
.font-change-controls,
.passage-tools,
.passage-options .icon-share2,
.passage-resources-link.open-link,
.sidebar-wrap,
.prev-chapter,
.next-chapter,
.passage-scroller,
.passage-scroller-top,
.passage-display,
FOOTER,
.bga-copyright,
.passage-other-trans,
.qtip-content HR,
.votd,
.votd-subscribe-long,
.headlines{
  display: none !Important;
}

}
