}

.slBtn {
	margin: -2px 0 0 0 !important;
	border-radius: 1px;
	font-size: 10px;
	height: 22px;
	line-height: 19px;
	min-width: 32px;
	padding: 0px 5px;
}

.slBtn .caret {
	margin-top: 7px;
}

.slSendDropdown .dropdown-menu,
.slRemindDropdown .dropdown-menu {
	font-size:12px;
}

.slSendDropdown .dropdown-menu .divider,
.slRemindDropdown .dropdown-menu .divider {
	margin: 2px 1px;
}

.slSendDropdown li.slDateHuman, .slRemindDropdown li.slDateHuman {
	margin-bottom: 1px;
}
.slSendDropdown .slDateFormatted, .slRemindDropdown .slDateFormatted {
	font-size: 10px;
	text-align: center;
	line-height: 10px;
	padding: 1px 0;
}

.slDraftSend .slDraftToolbar {
	padding: 6px 4px 4px 4px;
}

.slSnippetsInput>input {
	margin: 0;
}
}
