css:
@-moz-document domain(duolingo.com) {


.topbar-blue {
    background-color: rgba(255, 0, 0, 0.8);
    background-image: linear-gradient(to bottom, #E92020, rgba(220, 30, 30, 0.5));
}
.topbar-nav-main>li.active a {
    color: #fff;
    background: #D11414;
}
.topbar-nav-main>li.active a {
    color: #fff;
    background: #D11414;
}
.topbar-nav-main>li a:hover {
    color: #fff;
    background: #D11414;
}

.btn-notifications {
    background: #D01313;
}
.btn-primary,
.btn-blue {
    background: #F61C1C;
    color: #fff;
    border-color: #F61C1C;
}
.btn-primary:hover,
.btn-blue:hover {
    background: #F73E3E;
    border-color: #F73E3E;
}
.list-notifications a {
    color: #F61C1C;
}
.user-social-links a {
    background: #F61C1C;
}
.user-social-links a:hover {
    background: #F73E3E;
}
.username {
    color: #F61C1C;
}
.btn-strengthen-skills .strength {
    background: #D11414;
}
.fluency-container .fluency-score-actions .share-action .fluency-score-link-text {
    color: #F61C1C;
}

.nav-tabs>li.active a,
.nav-tabs>li:hover a,
.nav-tabs>li.active button,
.nav-tabs>li:hover button {
    color: #F61C1C;
    border-bottom: 2px solid #F61C1C;

}
.btn-green {
    background: #FF8100;
    border-color: #FF8100;

}
.btn-green:hover {
    background: #FF9E3A;
    border-color: #FF9E3A;
}

  /*///////////////////////////////////////
  // Delete this if you want default Duo //
  ///////////////////////////////////////*/

.owl {
    -webkit-filter: hue-rotate(310deg);
}

  /*///////////////////////////////////////
  // Delete this if you want default Duo //
  ///////////////////////////////////////*/

}
