css:
@-moz-document domain("travis-ci.com"), domain("travis-ci.org") {
body, .top .cta, #left, .tile--sidebar, .feedback-button {
    background-color: hsl(0,0%, 5%);
}

.sidebar-list ul {
    background-color: hsl(0,0%, 2%);
}

.top, .tile {
    background-color: hsl(0, 0%, 8%);
}

span.icon {
    -webkit-filter: invert(90%);
    filter: invert(90%);
}

span.icon--job {
    -webkit-filter: none;
    filter: none;
}

.btn, .search--sidebar .search-inner input {
    -webkit-filter: invert(80%);
    filter: invert(80%);
}

@media only screen and (min-width: 50.063em) {

    .tile--sidebar h2:after {
		background: linear-gradient(
        	-90deg,
        	rgba(255,255,255,0) 100%,
        	#fff 0
        );
	}

    .repo-main-description:after {
        background: linear-gradient(
            0deg,
            rgba(250,250,248,0) 100%,
            #fafaf8 0
        );
    }

    .tile--jobs .job-lang:after,
    .tile--jobs .job-env:after,
    .tile--jobs .job-anchor:after,
    .repo-main-commit:after,
    .repo-main-info:after {
        background: linear-gradient(
            -90deg,
            rgba(250,250,248,0) 100%,
            #fafaf8 0
        );
    }
}


}
