css:
@-moz-document domain("visualstudio.com") {
    ::-webkit-scrollbar {
        width: 12px;
    }

    ::-webkit-scrollbar-track {
        -webkit-box-shadow: inset 0 0 60px rgba(50,50,50,0.7);
        border-radius: 10px;
    }

    ::-webkit-scrollbar-thumb {
        -webkit-box-shadow: inset 0 0 6px rgba(200,200,200,1);
    }
    .work-item-form-main-header {
        border-left-color: #2E2E2E;
    }
    .work-item-form-id a{
        color:#FFFFFF
    }
    .work-item-form-main-core *, .witform-layout *{
        color: #BBBBBB;
        background-color: #2E2E2E;
    }
    .work-item-form-main-core, .witform-layout {
        border: none;
        padding-top:0px!important;
    }

    .witform-layout-content-container{
        margin-top: 10px;
    }

    .work-item-form-tab{
        background-color: #2E2E2E!important;
        border: none!important;
    }

    .control .wrap{
        border: 1px solid #aaa;
    }
    .header-post11 #navWrapper, .header-post11 #navWrapper *{
        background-color: #2E2E2E!important;
    }

    div#header-row {
        background-color: #007ACC;
    }

    .header-post11 .header-search {
        background-color: #2E2E2E;
        border: 1px solid #BBBBBB;
    }

    .left-hub-content {
        background-color: #1E1E1E;
        top: 0;
    }

    .left-hub-content .node-content .text {
        color: #BBBBBB;
    }

    .left-hub-content .tree-children {
        color: #007ACC;
    }

    .left-hub-content .ui-droppable {
        background-color: #1E1E1E;
    }

    .tree-drag-tile.folder > .node-link > .node-content, .tree-view .folder > .node-link > .node-content {
        background-color: #1E1E1E;
        color: #BBBBBB;
    }

    .hub-splitter.splitter.horizontal > .leftPane {
        border-right: 0;
    }

    .toolbar {
        border-bottom: 1px solid #BBBBBB;
        border-top: 0;
        color: #BBBBBB;
    }

    .source-explorer-tree.with-favorites .repository-node {
        border-top: 0;
    }

    .query-folder-tree .folder.top-separator {
        border: 0;
    }

    .splitter.horizontal > .handleBar {
        background-color: #2E2E2E;
    }

    .responsive-grid .grid-cell .grid-cell-title {
        border-bottom: 1px solid #BBBBBB;
    }

    .node-link > .node-content, .grid-cell a{
        /*background-color: #1E1E1E!important;*/
        color: #BBBBBB!important;
    }

    .dashboard-landing .hub-content{
        /*background-color: #2E2E2E!important;*/
        background-color: #1E1E1E!important;
        color: #BBBBBB;

    }
    .hub-content{
        background-color: #1E1E1E;
        color: #BBBBBB;
    }

    .vc-history-list .results-container .group-header {
        background-color: #2E2E2E;
    }

    .vc-history-list .results-container .history-result .result-details .change-link-container .more-comment-link {
        background-color: #2E2E2E;
    }

    .hub-title {
        background-color: #1E1E1E;
        color: #BBBBBB;
        margin-left: 0;
        margin-right: 0;
        padding-left: 20px;
        padding-right: 20px;
    }

    .ui-dialog > * {
        background-color: #1E1E1E!important;
        color: #BBBBBB;
    }
    .workitem-header-bar {
        background-color: #1E1E1E!important;
    }


    .menu-item.toggle-on {
        background-color: #2E2E2E;
    }

    .add-panel {
        background-color: #2D2D30;
    }

    .combo > div.wrap > input.invalid, .combo.invalid > div.wrap > input {
        background-color: #2E2E2E;
    }

    .tree-view > ul.tree-children li.selected > a > div.node-content, .tree-view.focus > ul.tree-children li.selected > a > div.node-content, li.tree-drag-tile > a > div.node-content {
        background-color: #2E2E2E;
        border: 1px solid #2E2E2E;
    }

    .sidebar-search .search-input-wrapper {
        background-color: #2E2E2E;
        border: 1px solid #2E2E2E;
    }

    .bowtie-folder{
        color: #DCB67A!important;
    }

    .bowtie-git {
        color: #F05133!important;;
    }
    .watermark, .bowtie-icon{
        color: #BBBBBB;
    }

    .selected-item-text{
        color: #BBBBBB!important;
    }
    .filtered-list-popup{
        background-color: #e6e6e6;
    }
    .text-filter-box {
        background-color: #1E1E1E;
    }

    .text-filter-box input {
        color: #BBBBBB;
    }

    button, button.ui-state-default, button.ui-button {
        background-color: #2E2E2E;
        border: 0;
        color: #BBBBBB;
    }

    .tag-container {
        border: 1px solid #2E2E2E;
    }

    .tags-add-button .tag-box-selectable {
        background-color: #2E2E2E;
        color: #BBBBBB;
    }

    .combo, .mru-identity-picker {
        background: #2E2E2E;
        border: 1px solid #2E2E2E;
    }

    .ui-tabs .ui-tabs-nav {
        background-color: #2E2E2E;
    }

    .splitter.horizontal > .rightPane {
        background-color: #1E1E1E;
        color: #BBBBBB;
    }

    .hub-pivot-content {
        background-color: #1E1E1E;
    }

    .rendered-markdown {
        color: #BBBBBB;
    }

    .rendered-markdown h2 {
        color: #BBBBBB;
    }

    .hub-title-right, .hub-title-right-secondary {
        color: #BBBBBB;
    }

    .views a {
        color: #BBBBBB;
    }

    .pivot-filter .title {
        color: #BBBBBB;
    }

    .filters a {
        color: #007ACC;
    }

    th .witState {
        color: #BBBBBB;
    }

    .tbPivotItem .witTitle {
        color: #BBBBBB;
    }

    .taskboard-cell {
        background-color: #2E2E2E;
        border-bottom: 1px solid #1E1E1E;
        border-left: 1px solid #1E1E1E;
        border-right: 1px solid #252525;
    }

    .ui-droppable {
        background-color: #1E1E1E;
    }

    .taskboard-expander {
        background-color: #1E1E1E;
        border-bottom: 1px solid #1E1E1E;
    }

    .taskboard-content-row .taskboard-expander, .taskboard-content-row .taskboard-parent {
        background-color: #1E1E1E;
    }

    .tbTile .tbTileContent {
        background-color: #2E2E2E;
        border: 0;
    }

    .tbTile .clickableTitle {
        color: #BBBBBB;
    }

    .field-container.assignedTo {
        color: #BBBBBB;
    }

    .tbTile .witTitle {
        color: #BBBBBB;
    }

    .tbTile .witAssignedTo {
        color: #BBBBBB;
    }

    .parentTbTile .witState {
        color: #BBBBBB;
    }

    .board-add-card {
        background-color: #3E3E3E;
        border: solid 1px #007ACC;
    }

    .agile-board .horizontal .horizontal-table > .row > .cell:first-of-type,
    .agile-board .horizontal .horizontal-table > .row > .cell {
        border-bottom: 1px solid #252525;
        border-left: 1px solid #1E1E1E;
        border-right: 1px solid #252525;
    }

    .agile-board .horizontal .horizontal-table > .row.header > .cell {
        background-color: #2E2E2E;
        border-bottom: 1px solid #1E1E1E;
    }

    .agile-board .member.header {
        color: #BBBBBB;
    }

    .agile-board .horizontal > .agile-content-container > .content-container-innerDiv-class .horizontal-table {
        background-color: #1E1E1E;
    }

    .agile-board .board-tile, .board-tile.ui-draggable-dragging {
        background-color: #2E2E2E;
    }

    .agile-board .board-tile .board-tile-content {
        border: 0;
    }

    .board-tile .assignedTo {
        color: #BBBBBB;
    }

    .versioncontrol-explorer-view .source-path {
        background-color: #2E2E2E;
        border: 0;
    }

    label, legend {
        color: #BBBBBB;
    }

    input, textarea, select {
        background-color: #2E2E2E;
        color: #BBBBBB;
    }

    body[contenteditable]{
            background-color: #2E2E2E;
            color: #BBBBBB;
    }

    body[contenteditable] td{
        color: #000;
    }

    .grid-header {
        color: #BBBBBB;
    }

    .grid-gutter-row-current {
        background-color: #1E1E1E;
        border: 1px solid #1E1E1E;
    }

    .grid-gutter-row-selected {
        background-color: #1E1E1E;
        border: 1px solid #1E1E1E;
    }

    .grid-gutter-row-normal {
        background-color: #1E1E1E;
        border: 1px solid #1E1E1E;
    }

    .grid-row {
        color: #BBBBBB;
    }

    .grid-row-current {
        border: 1px solid #2E2E2E;
    }

    .grid-row-selected-blur {
        background-color: #2E2E2E;
        color: #BBBBBB;
    }

    .vc-history-basic-filter .selected-filter {
        color: #BBBBBB;
    }

    .vc-changes-list-filter .advanced-filter input {
        border: 1px solid #BBBBBB;
    }

    .vc-changes-list-filter .advanced-filter .combo {
        border: 1px solid #BBBBBB;
    }

    .combo-drop-popup {
        background-color: #2E2E2E;
        border: 0;
    }

    .chart-hosts-list > .message-block.invalid {
        background-color: #2E2E2E;
        border: 0;
    }

    .tag-box, .tag-delete {
        background-color: #2E2E2E;
        color: #BBBBBB;
    }

    .mapping-pane-title {
        color: #BBBBBB;
    }

    .witform-layout-header-color {
        padding-left: 0px!important;
    }
    .work-item-view legend {
        color: #BBBBBB;
    }

    .ui-tabs .ui-tabs-nav li.ui-tabs-active a {
        color: #BBBBBB;
    }

    .query-type {
        border-bottom: 1px solid #BBBBBB;
    }

    .query-type .pivot-filter li .text {
        color: #BBBBBB;
    }

    .query-type .pivot-filter li.selected {
        background-color: #2E2E2E;
        border: 0;
    }

    .query-type .pivot-filter li.selected .text {
        color: #BBBBBB;
    }

    .query-editor .toolbar.work-item-list-toolbar {
        border-bottom: 1px solid #BBBBBB;
    }

    .textbox, .input-text {
        border: 0;
    }

    .query-editor .filters {
        padding-bottom: 10px;
    }

    .splitter > .handleBar, .splitter > .handleBar-clone {
        background-color: #2E2E2E;
    }

    .ui-dialog {
        background-color: #1E1E1E;
        border: 0;
    }
    .ui-dialog .ui-dialog-title{
        color: #BBBBBB;
    }
    .ui-dialog .ui-dialog-titlebar{
        background-color: #1E1E1E;
    }

    .ui-dialog-buttonpane {
        background-color: #1E1E1E;
        border: 0;
    }

    .ui-state-disabled, .ui-widget-content .ui-state-disabled, .ui-widget-header .ui-state-disabled {
        background-color: #2E2E2E;
        border: 0;
        color: #3E3E3E;
    }

    .ui-widget-content {
        color: #BBBBBB;
    }
}
