css:
@-moz-document domain(youtube.com){
.options-renderer-submit-button {
  	border-color: #167ac6 !important;
    background: #167ac6 !important;
    border: solid 1px transparent !important;
    border-radius: 2px !important;
    box-shadow: 0 1px 0 rgba(0,0,0,0.05) !important;
}
body {
	background:0 0
}
.yt-uix-button {
    box-shadow: none!important;
    border: none
}
.yt-uix-button-default:active {
    border: none!important;
    box-shadow: none!important
}
#yt-masthead #logo-container .logo {
    background: url(data:image/png;base64,) 5px 2px no-repeat!important;
    background-size: 64px 26px!important
}
#logo-container .content-region {
    display: none!important
}
.yt-lockup-byline .yt-channel-title-icon-verified,
.yt-uix-subscription-button::before {
    display: none
}
.pl-video .pl-video-thumbnail .watched-badge {
    line-height: 42px;
    font-size: 10px;
    left: 0;
    top: 0;
    max-width: 100%;
    width: 100%;
    padding: 0;
}
#yt-masthead-container {
    background: /*[[accent]]*/;
    border: none;
    height: 34px;
    border-bottom: 1px solid /*[[accent]]*/;
}
#yt-masthead {
    height: 36px
}
#yt-masthead-content {
    height: 32px
}
#masthead-search {
    height: 25px;
    border-bottom: 1px solid #FFF;
    top: 3px
}
#masthead-search,
iframe.gstl_50,
table.gstl_50.gssb_c {
    width: 652px !important;
    max-width: 100% !important;
    left: 50%;
    transform: translate(-50%, 0);
    -webkit-transform: translate(-50%, 0);
    -moz-transform: translate(-50%, 0);
    -o-transform: translate(-50%, 0);
    -ms-transform: translate(-50%, 0);
}
iframe.gstl_50,
table.gstl_50.gssb_c {
    margin-top: 4px !important
}
.gssb_m {
    padding: 8px 0 !important
}
.gssb_m tbody tr {
    display: block;
    padding: 2px 0 !important
}
.gssb_a {
    padding: 0 10px;
}
.gssb_e {
    padding: 0;
}
#masthead-search-terms {
    background: 0 0;
    border: 0;
    box-shadow: none
}
input#masthead-search-term {
    color: #FFF;
    font-size: 15px
}
input#masthead-search-term {
    color: #FFF;
    font-size: 15px
}
input#masthead-search-term::-webkit-input-placeholder {
	color: #FFF;
}
input#masthead-search-term::-moz-placeholder {
    color: #FFF;
    opacity: 1;
}
input#masthead-search-term:-moz-placeholder {
    color: #FFF;
    opacity: 1;
}
input#masthead-search-term:-ms-input-placeholder {
    color: #FFF;
}
.gsok_a {
    background: url(data:image/png;base64,) 0 9px no-repeat !important;
    height: 26px!important
}
#masthead-search #search-btn {
    background: rgba(0, 0, 0, .1);
    width: 30px;
    border: none;
    border-radius: 0;
    height: 26px
}
#masthead-search #search-btn:hover {
    background: rgba(0, 0, 0, .2)
}
#masthead-search .search-btn-component .yt-uix-button-content {
    width: 15px;
    height: 15px;
    background: url(data:image/png;base64,) no-repeat!important;
    background-size: 15px 15px!important;
    opacity: 1
}
#masthead-positioner:hover .yt-uix-button-icon-appbar-guide,
.yt-uix-button-icon-appbar-guide {
    background: url(data:image/png;base64,) no-repeat;
    background-size: 15px 15px
}
#yt-masthead .search-btn-component .yt-uix-button-content {
    margin: 0 8px!important
}
.guide-pinned.show-guide .guide-pinning-enabled #appbar-content {
    padding: 0
}
#masthead-appbar {
    border: 0
}
a.yt-uix-button-epic-nav-item:hover,
h2.epic-nav-item-heading {
    border-color: /*[[accent]]*/ !important;
}
#upload-btn.yt-uix-button,
.signin-container .yt-uix-button,
#upload-btn.yt-uix-button:hover
.signin-container .yt-uix-button:hover {
    background: rgba(0, 0, 0, .1);
    border-radius: 0;
    border: transparent;
    border-bottom: 1px solid #FFF!important;
    color: #FFF
}
#upload-btn.yt-uix-button:hover,
.signin-container .yt-uix-button:hover {
    background: rgba(0, 0, 0, .2)
}
#yt-masthead-user {
    margin-top: 4px
}
#yt-masthead-user .yt-uix-button-icon-bell {
    background: url(data:image/png;base64,) 4px 8px no-repeat;
    background-size: 17px 19px;
    opacity: 1
}
#page {
    background: #FFF
}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: none
}
#appbar-guide-menu .guide-section-separator {
    border: 0;
    height: 16px
}
.guide-item.guide-item-selected {
    background-color: /*[[accent]]*/;
}
.guide-item {
    color: #888;
    font-size: 11px
}
.guide-item .guide-count {
    color: #B8B8B8;
    font-size: 9px
}
.yt-card {
    box-shadow: none
}
#footer-main .yt-uix-button,
.comment-section-sort-menu .yt-uix-button,
.yt-uix-button-expander,
.yt-uix-load-more,
.paginator.load-comments {
    background: #d9d9d9;
    border-radius: 0!important;
    border: transparent;
    border-bottom: 1px solid #FFF!important;
    color: #000
}
#footer-main .yt-uix-button:hover,
.yt-uix-load-more:hover {
    background: #bfbfbf
}
.watched-badge {
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    text-align: center;
    line-height: 110px;
    font-size: 24px;
    background: #000;
    opacity: .7;
    font-weight: 200
}
.watched .video-thumb {
    opacity: 1
}

.pl-video-thumb .yt-uix-button,
.thumb-wrapper .yt-uix-button,
.yt-lockup-thumbnail .yt-uix-button {
    opacity: .4
}

.pl-video-thumb .yt-uix-button:hover,
.thumb-wrapper .yt-uix-button:hover,
.yt-lockup-thumbnail .yt-uix-button:hover {
    opacity: 1
}
.video-time {
    opacity: 0;
    bottom: 0;
    right: 0;
    padding: 4px 10px
}
.yt-lockup:hover .video-time {
    opacity: 1
}
.branded-page-module-title {
    font-size: 20px;
    padding-bottom: 24px
}
.branded-page-module-title-text,
.shelf-annotation {
    color: #888;
    font-weight: 200
}
.yt-subscriber-count,
.yt-uix-subscription-button {
    border-radius: 0;
    background: #F2F2F2
}
.branded-page-related-channels .yt-uix-subscription-button {
    background: #B8B8B8!important
}
.branded-page-related-channels .yt-uix-subscription-button:hover {
    background: #CC181E !important;
}
.yt-subscription-button-subscriber-count-branded-horizontal {
    border-color: #B8B8B8 !important;
    background: #B8B8B8;
    border-radius: 0;
    color: #FFF
}
.yt-subscriber-count,
.yt-uix-button-subscribed-branded {
    border-color: #F2F2F2
}
.yt-uix-button-subscribe-branded {
    background: #777
}
.yt-uix-subscription-preferences-button {
    border: 0;
    background: #FFF;
    padding: 0 5px!important
}
#results .branded-page-box,
.branded-page-v2-primary-col .branded-page-box,
.section-list li .item-section .branded-page-box {
    border: 0!important
}
#results .branded-page-box {
    margin: 25px 10px 30px!important;
    padding: 15px;
    background-color: #F2F2F2
}
#results .yt-lockup-video .yt-lockup-description,
.expanded-shelf-content-item .yt-lockup-description {
    opacity: 0;
    color: #777
}
#results .yt-lockup-video:hover .yt-lockup-description,
.expanded-shelf-content-item:hover .yt-lockup-description {
    opacity: 1
}
#comment-section-renderer .comment-renderer-reply {
    opacity: .3
}
#comment-section-renderer .comment-renderer-footer .sprite-dislike,
#comment-section-renderer .comment-renderer-footer .sprite-like {
    opacity: .1
}
.comment-thread-renderer .yt-uix-button {
    background: 0 0
}
#autoplay-checkbox-label,
#autoplay-checkbox-label .toggle {
    border-radius: 0
}
.pl-video,
.search-header {
    border: 0
}
#autoplay-checkbox-label .checked::before {
    margin-top: -2px
}
.yt-uix-checkbox-on-off label {
    background: /*[[accent]]*/ !important;
}
.autoplay-info-icon {
    display: block
}
.yt-lockup-channel {
    background: #F2F2F2;
    margin: 10px 0 5px
}
.branded-page-related-channels-list .yt-lockup-channel,
.yt-ui-ellipsis {
    background: 0 0
}
.yt-lockup-channel .yt-uix-button {
    background: #777;
    color: #FFF;
    padding-left: 5px
}
.yt-lockup-channel .yt-uix-button:hover {
    background: /*[[accent]]*/;
}
.yt-lockup-channel .yt-subscriber-count {
    background-color: #FFF
}
.yt-badge {
    height: 20px;
    border: 0;
    background-color: #B8B8B8;
    color: #FFF;
    line-height: 20px;
    padding: 0 6px
}
.yt-lockup-channel .yt-badge {
    background-color: #FFF;
    color: #777
}
.pl-video:nth-child(2n+1) {
    background-color: #FAFAFA
}
.channel-header-profile-image-container {
    top: 15px!important
}
.yt-lockup-title a {
    color: #000!important;
    font-weight: 200
}
.yt-lockup-byline a,
.yt-lockup:hover .yt-lockup-byline a {
    color: #999!important
}
#yt-picker-language-footer .yt-uix-button:hover,
.yt-picker-section .yt-picker-item:hover {
    background: 0 0
}
#watch7-sidebar .video-list-item .title {
    color: #000!important
}
#page {
    margin-top: -1px
}
.pl-video:hover .pl-video-owner a {
    color: #767676
}
.lohp-video-metadata .yt-user-name {
    color: #767676!important
}
.lohp-video-link {
    color: #000;
    text-decoration: none!important
}
.lohp-large-shelf-container:hover .lohp-video-link,
.lohp-medium-shelf:hover .lohp-video-link {
    text-decoration: underline
}
.lohp-video-link:hover {
    text-decoration: underline!important
}
li.guide-section h3 a {
	color: /*[[accent]]*/
}
.guide-item.guide-item-selected:hover {
	background: /*[[accent]]*/
}
#action-panel-details:hover a,
#footer-links-primary a:hover,
#footer-links-secondary a:hover,
#yt-picker-language-footer .yt-uix-button,
#watch-discussion .user-name,
.comment-author-text,
#watch-discussion .like-count,
.lohp-large-shelf-container:hover .lohp-video-link,
.lohp-medium-shelf:hover .lohp-video-link,
.pl-video:hover .pl-video-title-link,
.upload-informational-footer a,
.watch-card:hover a,
.yt-picker-section .yt-picker-item,
.yt-uix-expander .load-more-button,
.yt-uix-expander-head,
.video-player-view-component .title a {
    background: 0 0;
    color: /*[[link_accent]]*/
}
.yt-badge.comment-author-verified-badge {
	background-color: transparent;
}
.comment-renderer-author-comment-badge.creator > a {
	color: white;	
}
#results .item-section li:hover .yt-uix-tile-link,
#watch7-sidebar .video-list-item:hover .title,
.pl-video-owner a:hover,
.yt-lockup-video:hover .yt-lockup-title a,
.yt-shelf-grid-item:hover .yt-lockup-title a,
.yt-uix-redirect-link,
.comment-replies-header .load-comments a {
    color: /*[[link_accent]]*/!important
}
#comment-section-renderer-items .yt-thumb,
#guide-subscriptions-section .yt-thumb,
#yt-masthead-account-picker .yt-uix-button,
#yt-masthead-user .yt-masthead-user-icon,
.branded-page-module-title .yt-thumb,
.channel-header-profile-image,
.channel-header-profile-image-container,
.circled-user-profile .video-thumb,
.watch-card-lockup-container .yt-thumb,
.yt-lockup-channel .yt-thumb,
.user-photo,
#watch-header .yt-thumb {
  /*[[rounded_true]]*/
  /*[[rounded_false]]*/
}

.video-list-item .view-count {
    color: #B8B8B8 !important;
}


/* show views and date permanently or on hover */
/*[[info_permanent]]*/
/*[[info_hover]]*/




}
