css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("taskulu.com") {
  
  
  
  .list-status.status-1 {
	background-color:#5cc7ef;
}
  
.list-status.status-1:hover {
	background-color:#3890ec;
}  
  .progress-bar-1 {
    background-color: #5cc7ef;
}
  .settings-sections .settings-section-1 .settings-section-input {
    border-color: #5cc7ef;
}
  
  
  
  
 .list-status.status-2 {
    background-color: #002080;
}
  
  .list-status.status-2:hover {
    background-color: #0039e6;
}
  .progress-bar-2 {
    background-color: #002080;
}
  .settings-sections .settings-section-2 .settings-section-input {
    border-color: #002080;
}
  
  
  
  
   .list-status.status-3 {
    background-color: #24b4a5;
}
  
  .list-status.status-3:hover {
    background-color: #61af7d;
}
  .progress-bar-3 {
    background-color: #6f9ec4;
}
  .settings-sections .settings-section-3 .settings-section-input {
    border-color: #6f9ec4
}
  
  
  
  
   .list-status.status-4 {
    background-color: #6ca3cf;
}
  
  .list-status.status-4:hover {
    background-color: #9c8faf;
}
  .progress-bar-4 {
    background-color: #5c5c3d;
}
  .settings-sections .settings-section-4 .settings-section-input {
    border-color: #5c5c3d;
}
  
  
  
  
  
  .list-section.section-1, .list-section.section-1 .list-section-head {
    background-color: #5cc7ef;
    
}
  .list-section.section-2, .list-section.section-2 .list-section-head {
    background-color:#002080;
} 

  .list-section.section-3, .list-section.section-3 .list-section-head {
    background-color: #4ec6ae;
}
  .list-section.section-4, .list-section.section-4 .list-section-head {
    background-color: #859bdc;
}
  
  body {
    direction: rtl;
    font-family: yekan,"Open Sans","Helvetica Neue",Helvetica,Arial,sans-serif;
   
    background: transparent url(http://s3.picofile.com/file/8225774426/avini.jpg) no-repeat  center center / 100% auto;
}
  
  
  
  
  
  
  .project-main-inside {
    padding: 50px 30px 50px 70px;
    position: relative;
    background-color: azure;
}
  
  
  .list-section-title {
    height: 30px;
    font-size: 1.3rem;
    padding: 8px 10px 5px;
    font-weight: 400;
    color: #FFF;
}
  .list-header .list-title {
    padding: 9px 9px 9px 0px;
    max-width: 100%;
    font-size: 1.4rem;
    font-weight: 700;
    color: #000;
    width: auto;
    flex: 1 1 auto;
}
  
  .bar {
    box-shadow:0px -1px 0px 0px #576580 inset;
    position: absolute;
    top: 0px;
    right: 0px;
    width: 100%;
    height: 32px;
  background-color: #576580;
 
    z-index: 10;
}
  
  .list-section-icon {
    height: 30px;
    padding: 7px 5px 0px;
    color: azure;
}
  
  
  #ts-logo {
    display: none;
    width: 100%;
    height: 100%;
    
}
  
  
  
  
  .bar-info {
    line-height: 1.6em;
    color: #FFF;
    font-size: 1.1em;
    padding-right: 13px;
}
  
  .bar .bar-btn {
    color: #FFF;
}
  
  
  
  
  
  
#feedback-form-wrapper > div.feedback-btn {
    position: fixed;
    bottom: 10px;
    left: 10px;
    display: none;
    width: 34px;
    height: 34px;
    background-color: #FFF;
    border-radius: 50%;
    text-align: center;
    vertical-align: middle;
    line-height: 33px;
    font-size: 23px;
    perspective: 1000px;
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.25);
}
  
  
  deadline-reached {
    color: #D75454;
}
  
  
  

}
