css:
@namespace url(http://www.w3.org/1999/xhtml);
  
@-moz-document domain("tabun.everypony.ru") {

  /* КОСТЫЛЬ - отрубаем шапки и сайд */  
  #c-header, #sidebar, #toolbar.toolbar, .main-menu {
    display: none;
  }
  
  /* навигашка */
  #header {
    height: 80px;
    position: fixed;
    left: 0px;
    right: 0px;
    top: 0px;
    min-width: 0px;
    padding: 0px 64px 0px 12px;
  }
    
  #wrapper {
    /* чтобы начало контента не пряталось под зафиксированную шапку*/
    margin-top: 80px;
  }
   
  #header .dropdown-user {
    position: fixed;
    width: 64px;
    height: 64px;
    right: 0px;
    top: 0px;
    padding: 0px;
    margin: 0px;
  }  
    
  #header .dropdown-user ul, #header .dropdown-user .username {
    display: none;
  }
    
  #header .dropdown-user .avatar {
    display: inline-block;
    width: 64px;
    height: 64px;
    padding: 0px;
    margin: 0px;
    border: 0px;
  }
    
  .nav.nav-main li a {
    font-size: 20px;
  }
    
  .nav.nav-main li {
    height: 30px;
    line-height: 28px;
  }
  
  .nav.nav-main {
    height: auto;
  } 
    
  nav#nav {
    position: fixed;
    top: 80px;
    margin: -24px 0px 0px 0px;
    padding: 0px;
    min-height: 0px;
    left: 12px;
    right: 12px;
  }
    
  /* попытка в USERMENU */ 
  #dropdown-user.dropdown-user:hover ul#dropdown-user-menu.dropdown-user-menu {
    display: inline-block;
    margin: 0px 12px 12px -24px;
    background: #fff;
    z-index: -30000;
  }
    
  ul.dropdown-user-menu li {
    padding: 8px;
    font-size: 12px;
    float: left;
  }
    
  /* ширина */
  body {
    font-size: 12px;
  }
  
  #container, body, #wrapper {
    min-width: 320px;
    padding: 0px;
    margin: 0px;
  }
  
  #content-wrapper {
    width: 100%;
  }
  
  #content {
    padding: 0px 6px;
  }
  
  div#widemode {
    padding: 6px;
    margin: 0px;
    border-radius: 0px;
    display: block;
    position: fixed;
    bottom: 0px;
    left: 0px;
    right: 0px;
  }  
    
  .comment-wrapper {
    padding-left: 12px;
  }  
    
  .comment {
    min-width: 240px;
  }

}
