css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.co.nz"), domain("google.com"), domain("google.com.au"), domain("google.co.uk") {
  *
  {
    font-family: roboto !important;
  }
  
  #viewport
  {
    background: rgb(240, 240, 240);
  }
  
  div[nowrap=""]
  {
    display: none !important;
  }
  
  div#gb
  {
    overflow-x: hidden;
  }
  
  div[style="min-width: 305px;"]
  {
    transition: 150ms;
    transition-delay: 2000ms;
    transform: translateX(305px);
    opacity: 0;
  }
  
  div#gb:hover div[style="min-width: 305px;"]
  {
    transition: 150ms;
    transform: none;
    opacity: 1;
  }
  
  div#fbar
  {
    overflow-y: hidden;
    background: transparent;
    border: none;
  }
  
  div.fbar
  {
    transition: 150ms;
    transform: translateY(50px);
    opacity: 0;
  }
  
  div#fbar:hover div.fbar
  {
    transition: 150ms;
    transform: none;
    opacity: 1;
  }
  
  div#als
  {
    display: none;
  }
  
  input[value="I'm Feeling Lucky"]
  {
    display: none;
  }
  
  input[value="Google Search"]
  {
    display: none;
  }
  
  div.sbibod
  {
    transition: 350ms;
    border: none !important;
    outline: none !important;
    animation: slide-fade-in 750ms;
    margin-top: 0px;
    
    border-bottom: 3px solid rgb(230, 230, 230) !important;
    border-left: 2px solid rgb(235, 235, 235) !important;
    border-right: 2px solid rgb(235, 235, 235) !important;
    border-radius: 0px 0px 2px 2px;
  }
  
  div.sbfcn
  {
    transition: 500ms;
    box-shadow: 0px 7px 10px rgba(100, 100, 100, 0.1);
  }
  
  div.sfbg
  {
    margin-top: -45px !important;
  }
  
  form#tsf
  {
    margin-top: -30px !important;
  }

  div#sblsbb
  {
    border-radius: 0px 50% 50% 0px !important;
    padding-right: 5px;
  }
  
  @keyframes slide-fade-in
  {
    from { margin-top: 60px; opacity: 0; }
    to { margin-top: 0px; opacity: 1; }
  }
}
