css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rollin.io") {
  
  .button.black {
    background: none;
    background-color: #333;
    color: #aaa;
    text-shadow: none;
    box-shadow: none;
  }
  
  .button.yellow {
    background: none;
    background-color: #593B00;
    color: #aaa;
    text-shadow: none;
    box-shadow: none;
  }

  #chat-popup .messages {
    background-color: rgb(20, 20, 20);
    color: #bbb;
  }
  
  #chat-popup .messages .sender {
    color: #bbb;
  }
  
  #chat-popup header {
    background-color: rgb(30, 30, 30);
    color: #bbb;
    border-radius: 0;
  }
  
  #chat-popup header h5 {
    color: #333;
    text-shadow: none;
  }
  
  #chat-popup .rooms-wrap {
    background-color: rgb(30, 30, 30);
    color: #bbb;
    box-shadow: none;
  }
  
  .popup header .close {
    border-color: #202020;
  }
  
  #chat-popup .message {
    background-color: rgb(30, 30, 30);
    color: #bbb;
    border-radius: 0;
  }
  
  #chat-popup .send {
    color: #BF592A;
  }

    
  #chat-popup .messages li {
    border-top: 1px solid #202020;
  }
  
  .controls .control, .controls .tabs .tab {
    box-shadow: none;
    background: #222;
  }
  
  .controls button {
    color: #ddd;
  }
 
  .controls p {
    color: #bbb;
  }
  
  .controls .tabs p.tab {
      padding: 1px 0px 1px;
  }
  
  .controls footer {
    background: none;
  }
  
  .controls input {
    border: none;
    background-color: rgb(20, 20, 20);
    color: #bbb;
  }

}
